constructOptionList(this.props.Login.masterData.ProjectMasterList||[],"nprojectmastercode","sprojectcode","nprojectmastercode","ascending",false);project=projectMasterMap.get("OptionList");tempFilterData["nprojectmastercode"]=project.length>0?project[0]:undefined;isStateChanged=true;}var treeVersionTemplate=this.state.treeVersionTemplate||[];if(this.props.Login.masterData.TreeVersionTemplate!==previousProps.Login.masterData.TreeVersionTemplate){var templateMap=constructOptionList(this.props.Login.masterData.TreeVersionTemplate||[],"ntreeversiontempcode","sversiondescription","ntreeversiontempcode","ascending",false);treeVersionTemplate=templateMap.get("OptionList");tempFilterData["ntreeversiontempcode"]=treeVersionTemplate.length>0?treeVersionTemplate[treeVersionTemplate.length-1]:"";isStateChanged=true;}// if (this.props.Login.masterData.TestGroupTestParameter && this.props.Login.masterData.TestGroupTestParameter !== previousProps.Login.masterData.TestGroupTestParameter) {
//     sortData(this.props.Login.masterData.TestGroupTestParameter, "ascending", "nsorter");
// }
