//     sortData(this.props.Login.masterData.TestGroupTesTest, "ascending", "nsorter");
// }
var userRoleControlRights=this.state.userRoleControlRights||[];var controlMap=this.state.controlMap||[];if(this.props.Login.userInfo&&this.props.Login.userInfo.nformcode!==previousProps.Login.userInfo.nformcode){var nformCode=this.props.Login.userInfo.nformcode;if(this.props.Login.userRoleControlRights){this.props.Login.userRoleControlRights[nformCode]&&Object.values(this.props.Login.userRoleControlRights[nformCode]).map(function(item){return userRoleControlRights.push(item.ncontrolcode);});}controlMap=getControlMap(this.props.Login.userRoleControlRights,nformCode);filterData=_objectSpread({},tempFilterData);this.generateBreadCrumData(filterData);}if(this.props.Login.filterData!==previousProps.Login.filterData){filterData=_objectSpread({},this.props.Login.filterData);this.generateBreadCrumData(filterData);isStateChanged=true;}else{this.generateBreadCrumData(filterData);}// else if (filterData !== previousState.filterData) {
//     filterData = { ...tempFilterData };
//     this.generateBreadCrumData(filterData);
// }
var historyDataState=this.state.historyDataState;if(this.props.Login.historyDataState&&this.props.Login.historyDataState!==previousProps.Login.historyDataState){historyDataState=this.props.Login.historyDataState;isStateChanged=true;}var _this$state=this.state,testskip=_this$state.testskip,testtake=_this$state.testtake;if(this.props.Login.masterData!==previousProps.Login.masterData){testskip=this.props.Login.testskip===undefined?testskip:this.props.Login.testskip;testtake=testtake?testtake:this.props.Login.testtake;isStateChanged=true;}var _this$state2=this.state,skipRulesEngine=_this$state2.skipRulesEngine,takeRulesEngine=_this$state2.takeRulesEngine;if(this.props.Login.masterData!==previousProps.Login.masterData){skipRulesEngine=this.props.Login.skipRulesEngine===undefined?skipRulesEngine:this.props.Login.skipRulesEngine;takeRulesEngine=takeRulesEngine?takeRulesEngine:this.props.Login.takeRulesEngine;isStateChanged=true;}if(isStateChanged){this.setState({selectedRecord:selectedRecord,tempFilterData:tempFilterData,filterData:filterData,userRoleControlRights:userRoleControlRights,controlMap:controlMap,sampleType:sampleType,productCategory:productCategory,product:product,treeVersionTemplate:treeVersionTemplate,historyDataState:historyDataState,testskip:testskip,testtake:testtake,projectType:projectType,project:project,activeTabIndex:activeTabIndex,activeTabId:activeTabId,skipRulesEngine:skipRulesEngine,takeRulesEngine:takeRulesEngine,selectedsubcodedresult:selectedsubcodedresult,selectsubcodedelete:selectsubcodedelete});}}}],[{key:"getDerivedStateFromProps",value:function getDerivedStateFromProps(props,state){if(props.Login.masterStatus!==""){if(props.Login.errorCode===417||props.Login.errorCode===409){toast.warn(props.Login.masterStatus);props.Login.masterStatus="";}}if(props.Login.error!==state.error){toast.error(props.Login.error);props.Login.error="";}return null;}}]);return TestGroup;}(Component);var mapStateToProps=function mapStateToProps(state){return{Login:st