        if (addGroupList[groupIndex] === 0) {
            addGroupList.splice(groupIndex, 1);
            selectedRecord["groupList"].splice(groupIndex, 1);
            if (selectedRecord["groupListJoins"]) {
                selectedRecord["groupListJoins"].splice(groupIndex - 1, 1);
