                break;

            case 2:
                tempFilterData[fieldName] = comboData;
                inputParam = {
                    inputData: {
                        userinfo: this.props.Login.userInfo,
                        sampletype: tempFilterData.nsampletypecode.item,
                        productcategory: comboData.item,
                        tempFilterData
                    },
                    methodUrl: "/getProduct"
                }
