  auditFieldConfigService,
  reloadDesignTemplateMapping,
  getTMPFilterRegType,
  getTMPFilterRegSubType,
  getTMPFilterSubmit,
  combinationUniqueConfigService
})(injectIntl(DesignTemplateMapping));

