                                                                            </Nav.Link>
                                                                            <Nav.Link className="btn btn-circle outline-grey mr-2"
                                                                                    hidden={this.state.userRoleControlRights.indexOf(subSampleEditFieldId) === -1}
                                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_CONFIGURSUBSAMPLEEEDITFIELDS" })}
                                                                                    data-for="tooltip_list_wrap"
                                                                                    onClick={() => this.editFieldConfiguration(subSampleEditFieldId, 'configuresubsampleedit')}
                                                                                >
                                                                                    <FontAwesomeIcon icon={faList} title={this.props.intl.formatMessage({ id: "IDS_CONFIGURSUBSAMPLEEEDITFIELDS" })} />
                                                                                </Nav.Link>
                                                                            </>
                                                                            : ""}
