                    saveType, formRef, postParam, selectedRecord: this.state.selectedRecord,
                    searchRef: this.searchRef,
                    dataState: this.state.dataState
                }
            }
            else {
                inputData["nmaterialcode"] = this.props.Login.masterData.SelectedMaterial.jsondata.nmaterialcode
                inputParam = {
                    classUrl: "material",
                    methodUrl: "Material",
                    inputData: inputData,
                    operation: this.props.Login.operation,
                    saveType, formRef, postParam, selectedRecord: this.state.selectedRecord,
                    searchRef: this.searchRef,
                    dataState: this.state.dataState
