import React from 'react';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { Col, Row, FormGroup, Card } from 'react-bootstrap';
import FormInput from '../../components/form-input/form-input.component';
import { injectIntl } from 'react-intl';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import FormNumericInput from '../../components/form-numeric-input/form-numeric-input.component';

const AddTestGroupNumericSpecification = (props) => {
    const { needActualResult, grade,gradenew} = props.parameterData;
    
   
    return (
        <Col md="12">
            <FormSelectSearch
                formLabel={props.intl.formatMessage({ id: "IDS_GENDER" })}
                isSearchable={true}
                name={"ngendercode"}
                // isDisabled={needActualResult}
                placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                // isMandatory={!needActualResult}
                isMandatory={true}
                options={grade || []}
                optionId='ngendercode'
                optionValue='sgendername'
                value={props.selectedRecord && props.selectedRecord["ngendercode"] ? props.selectedRecord["ngendercode"] : ""}
                onChange={value => props.onComboChange(value, "ngendercode", 1)}
                alphabeticalSort={true}
            >
            </FormSelectSearch>


            <FormNumericInput
                name={"nfromage"}
                label={props.intl.formatMessage({ id: "IDS_FROMAGE" })}
                type="number"
                value={props.selectedRecord["nfromage"]}
                placeholder={props.intl.formatMessage({ id: "IDS_FROMAGE" })}
                strict={true}
                min={0}
                isMandatory={true}
                //max={9999999.99}
                maxLength={3}
                onChange={(value) => props.onNumericInputChange(value, "nfromage")}
                noStyle={true}
                className="form-control"
                errors="Please provide a valid number."
            />


            <FormNumericInput
                name={"ntoage"}
                label={props.intl.formatMessage({ id: "IDS_TOAGE" })}
                type="number"
                value={props.selectedRecord["ntoage"]}
                placeholder={props.intl.formatMessage({ id: "IDS_TOAGE" })}
                strict={true}
                min={0}
                isMandatory={true}
                //max={9999999.99}
                maxLength={3}
                onChange={(value) => props.onNumericInputChange(value, "ntoage")}
                noStyle={true}
                className="form-control"
                errors="Please provide a valid number."
            />

            <FormGroup>
                <Card>
                    <Card.Header>{props.intl.formatMessage({ id: "IDS_OUTERBAND" })}</Card.Header>
                    <Card.Body>
                        <Row>

                            <Col md={6}>
                                <FormNumericInput
                                    name={"nlowb"}
                                    label={props.intl.formatMessage({ id: "IDS_LOWB" })}
                                    type="number"
                                    value={props.selectedRecord["nlowb"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_LOWB" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "nlowb")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />

                            </Col>



                            {/* <Col md={6}>
                                <FormNumericInput
                                    name={"nhigha"}
                                    label={props.intl.formatMessage({ id: "IDS_HIGHA" })}
                                    type="number"
                                    value={props.selectedRecord["nhigha"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_HIGHA" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "nhigha")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />
                            </Col> */}
                            <Col md={6}>
                                <FormNumericInput
                                    name={"nhighb"}
                                    label={props.intl.formatMessage({ id: "IDS_HIGHB" })}
                                    type="number"
                                    value={props.selectedRecord["nhighb"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_HIGHB" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "nhighb")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />


                            </Col>
                        </Row>
                    </Card.Body>
                </Card>
            </FormGroup>
            <FormGroup>
                <Card>
                    <Card.Header>{props.intl.formatMessage({ id: "IDS_INNERBAND" })}</Card.Header>
                    <Card.Body>
                        <Row>
                            <Col md={6}>
                                <FormNumericInput
                                    name={"nlowa"}
                                    label={props.intl.formatMessage({ id: "IDS_LOWA" })}
                                    type="number"
                                    value={props.selectedRecord["nlowa"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_LOWA" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "nlowa")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />

                            </Col>
                            <Col md={6}>
                                <FormNumericInput
                                    name={"nhigha"}
                                    label={props.intl.formatMessage({ id: "IDS_HIGHA" })}
                                    type="number"
                                    value={props.selectedRecord["nhigha"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_HIGHA" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "nhigha")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />
                            </Col>
                            {/* <Col md={6}>
                                <FormNumericInput
                                    name={"nlowb"}
                                    label={props.intl.formatMessage({ id: "IDS_LOWB" })}
                                    type="number"
                                    value={props.selectedRecord["nlowb"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_LOWB" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "nlowb")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />

                            </Col> */}
                        </Row>

                    </Card.Body>
                </Card>
            </FormGroup>

            <FormGroup>
            <Card>
                    <Card.Body>
                        <Row>
                            <Col md={6}>
                            <FormNumericInput
                                    name={"sresultvalue"}
                                    label={props.intl.formatMessage({ id: "IDS_DEFAULTRESULT" })}
                                    type="number"
                                    value={props.selectedRecord["sresultvalue"]}
                                    placeholder={props.intl.formatMessage({ id: "IDS_DEFAULTRESULT" })}
                                    strict={true}
                                    min={0}
                                    //max={9999999.99}
                                    maxLength={11}
                                    onChange={(value) => props.onNumericInputChange(value, "sresultvalue")}
                                    noStyle={true}
                                    precision={2}
                                    className="form-control"
                                    errors="Please provide a valid number."
                                />

                            </Col>

                            <Col md="6">
                <FormSelectSearch
                        formLabel={props.intl.formatMessage({ id: "IDS_GRADE" })}
                        isSearchable={false}
                        name={"ngradecode"}
                        isDisabled={props.selectedRecord["sresultvalue"] === null || props.selectedRecord["sresultvalue"] === ""  || props.selectedRecord["sresultvalue"] === undefined }
                        placeholder="Please Select..."
                        isMandatory={false}//{props.selectedRecord.sresultvalue !== null && props.selectedRecord.sresultvalue !== ""}
                        showOption={false}
                        options={gradenew|| []}
                        optionId='ngradecode'
                        optionValue='sgradename'
                        value={props.selectedRecord ? props.selectedRecord["ngradecode"] : ""}
                        onChange={value => props.onComboChange(value, "ngradecode", 1)}
                        alphabeticalSort={true}
                        //isClearable={true}
                    >
                    </FormSelectSearch> 
                            </Col>
                           
                            
                        </Row>

                    </Card.Body>
                    </Card>
            </FormGroup>
        </Col>
    );
}

export default injectIntl(AddTestGroupNumericSpecification);
