);}if(this.props.Login.masterData.registrationTypes){TaglistRegistrationType=constructOptionList(this.props.Login.masterData.registrationTypes||[],"nregtypecode","sregtypename",undefined,undefined,undefined);listRegistrationType=TaglistRegistrationType.get("OptionList");}if(this.props.Login.masterData.registrationSubTypes){TaglistRegistrationSubType=constructOptionList(this.props.Login.masterData.registrationSubTypes||[],"nregsubtypecode","sregsubtypename","nsorter","ascending",undefined);listRegistrationSubType=TaglistRegistrationSubType.get("OptionList");}if(this.props.Login.masterData.qualisforms){ListFormsMap=constructOptionList(this.props.Login.masterData.qualisforms||[],"nformcode","sdisplayname",undefined,undefined,undefined);listForms=ListFormsMap.get("OptionList");}}if(this.props.Login.fields!==previousProps.Login.fields){updateState=true;fields=this.props.Login.fields;}if(this.props.Login.sampleReportFilterTypeData!==previousProps.Login.sampleReportFilterTypeData){updateState=true;sampleReportFilterTypeData=this.props.Login.sampleReportFilterTypeData;}if(updateState){this.setState({selectedRecord:selectedRecord,controlMap:controlMap,userRoleControlRights:userRoleControlRights,listSampletype:listSampletype,listRegistrationType:listRegistrationType,listRegistrationSubType:listRegistrationSubType,selectedInput:selectedInput,listForms:listForms,fields:fields,sampleReportFilterTypeData:sampleReportFilterTypeData});}}},{key:"componentWillUnmount",value:function componentWillUnmount(){var updateInfo={typeName:DEFAULT_RETURN,data:{masterData:[],inputParam:undefined,operation:null,modalName:undefined,isInitialRender:true}};this.props.updateStore(updateInfo);}}],[{key:"getDerivedStateFromProps",value:function getDerivedStateFromProps(props,state){if(props.Login.masterStatus!==""&&props.Login.masterStatus!==state.masterStatus){toast.warn(props.Login.masterStatus);props.Login.masterStatus="";}if(props.Login.error!==state.error){toast.error(props.Login.error);props.Log