import React from 'react'
import { Card, Row, Col, FormGroup, FormLabel } from 'react-bootstrap'
import { injectIntl, FormattedMessage } from 'react-intl'
import { ReadOnlyText } from '../../components/App.styles';
import { designProperties } from '../../components/Enumeration';
class SampleInfoView extends React.Component {
    render() {
        const jsondata=this.props.data.jsondata?JSON.parse(this.props.data.jsondata.value):this.props.data
        return (
            <Card className="border-0">
                <Card.Body>
                    <Card.Text>
                        <Row>
                            {this.props.SingleItem && this.props.SingleItem.map((field,index) =>
                                <Col md={4} key={index}>
                                    <FormGroup>
                                        <FormLabel><FormattedMessage id={field} message={field} /></FormLabel>
                                        <ReadOnlyText>{this.props.data[field]?
                                        this.props.data[field]:
                                        (jsondata[field]).hasOwnProperty('label')?
                                        jsondata[field].label:
                                        jsondata[field]}</ReadOnlyText>
                                    </FormGroup>
                                </Col>
                            )}
                        </Row>
                    </Card.Text>
                </Card.Body>
            </Card>

        )
    }
}

export default injectIntl(SampleInfoView);
