import { SchedulerEditItem, SchedulerEditSlot, SchedulerTask, SchedulerViewSlot } from "@progress/kendo-react-scheduler";
import React from "react";
import { Popup } from "@progress/kendo-react-popup";
import { connect } from 'react-redux';
import { getInstrumentForSchedule } from '../../actions'
import { FormWithCustomEditor } from './custom-form';
import { toast } from "react-toastify";
import { Col, Row } from "react-bootstrap";
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { parseAdjust } from "./NewJobAlloct";

const mapStateToProps = state => {
    return ({ Login: state.Login })
}

const CustomEditItem = (props) => {
    const ref = React.useRef(null);
    const [show, setShow] = React.useState(false);
    const [formItem, setFormItem] = React.useState(null);
    const handleFocus = React.useCallback(
        (event) => {
            setShow(true);
            if (props.onFocus) {
                props.onFocus(event);
            }
        },
        [props]
    );
    const handleCloseClick = React.useCallback(() => {
        setShow(false);
    }, []);
    const handleEditClick = React.useCallback(() => {
        console.log("hello1", props.dataItem);
        if (props.dataItem &&
            props.dataItem.InstrumentCategory) {
            if (props.dataItem.ntransactionstatus) {
                if (props.dataItem.ntransactionstatus !== 20) {
                    props.getInstrumentForSchedule(props.dataItem.InstrumentCategory, props.Login.userInfo);
                    setFormItem(props.dataItem);
                    setShow(false);
                } else {
                    toast.info("Already allocted")
                }

            } else {
                props.getInstrumentForSchedule(props.dataItem.InstrumentCategory, props.Login.userInfo);
                setFormItem(props.dataItem);
                setShow(false);
            }

        }
       
    }, [props.dataItem]);
    const handleFormItemChange = React.useCallback((event) => {
        console.log("hello")
        console.log("handleFormItemChange", event.value)
        console.log("handleFormItemChange1", formItem)

        if(event.value){
            if(event.value.ntransactionstatus){
                if(event.value.ntransactionstatus!==20){
                    setFormItem(event.value);
                }else{
                    toast.info("Already Allocted")
                }

            }else{
                setFormItem(event.value);
            }

        }else{
            setFormItem(event.value);
        }

    }, []);
    return (
        <React.Fragment>
            <SchedulerEditItem
                ref={ref}
                {...props}
                onFocus={handleFocus}
                formItem={formItem}
                onFormItemChange={handleFormItemChange}
                form={FormWithCustomEditor}
            //    title={generateTitle(props.dataItem)}
            />
            <Popup anchor={ref.current && ref.current.element} show={show}>
                {console.log(props.dataItem)}
                <div className="p-1">
                    <h5>{"ArNo : "}{props.dataItem.sarno && props.dataItem.sarno}</h5>
                    <h5>{"Test Name : "}{props.dataItem.stestsynonym && props.dataItem.stestsynonym }</h5>
                    <h5 style={{"color":"red"}}>{"Status : "}{props.dataItem.stransdisplaystatus && props.dataItem.stransdisplaystatus}</h5>
                    <h5 style={{"color":"red"}}>{"Start Date : "}{ parseAdjust(props.dataItem.start).toLocaleDateString()}</h5>
                    <h5 style={{"color":"red"}}>{"End Date : "}{ parseAdjust(props.dataItem.end).toLocaleDateString()}</h5>
                    <a className="k-icon k-i-edit" onClick={handleEditClick} />
                    <a className="k-icon k-i-close" onClick={handleCloseClick} />
                </div>
            </Popup>
        </React.Fragment>
    );
};
export default connect(mapStateToProps, { getInstrumentForSchedule })(CustomEditItem);

export const CustomTask = (props) => {
    return (
        <SchedulerTask

            {...props}
            style={{
                ...props.style,
                backgroundColor: props.isAllDay ? "#ccccff" : "#ffcccc",
                borderRadius: "8px",
                height: 32,
            }}
        />
    );
};

export const CustomEditSlot = (props) => {
    const [formItem, setFormItem] = React.useState(null);
    const handleFormItemChange = React.useCallback(
        (event) => {
            if (props.isWorkDay && props.isWorkHour) {
                setFormItem(event.value);
            }
        },
        [props.isWorkDay, props.isWorkHour]
    );
    return (
        <SchedulerEditSlot
            {...props}
            formItem={formItem}
            onFormItemChange={handleFormItemChange}
        />
    );
};
export const CustomViewSlot = (props) => {
    return (
        <SchedulerViewSlot
            {...props}
            style={{
                ...props.style,
                cursor: !props.isWorkDay || !props.isWorkHour ? "no-drop" : undefined,
            }}
        />
    );
};

export const CustomEditSlotForMonth = (props) => {
    const [formItem, setFormItem] = React.useState(null);
    const handleFormItemChange = React.useCallback(
        (event) => {
            if(event.value){
                if(event.value.ntransactionstatus){
                    if(event.value.ntransactionstatus!==20){
                        setFormItem(event.value);
                    }else{
                        toast.info("Already Allocted")
                    }
    
                }else{
                    setFormItem(event.value);
                }
    
            }else{
                setFormItem(event.value);
            }
        },
        []
    );
    return (
        <SchedulerEditSlot
            {...props}
            formItem={formItem}
            onFormItemChange={handleFormItemChange}
        />
    );
};
export const CustomViewSlotForMonth = (props) => {
    // { console.log("slotmonth", props) }
    return (

        <SchedulerViewSlot
            {...props}
            style={{
                ...props.style,
                cursor: props.end < new Date() ? "no-drop" : undefined,
            }}

        />
    );
};


export const headerComponent = (props) => {
    return (

        <Row>
        <Col md={6}>
            {"hello"}
           </Col>
    </Row>
    );
};
