  componentDidUpdate(previousProps) {
    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      this.setState({
        selectedRecord: this.props.Login.selectedRecord
      });
    }

    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      const SampleMap = constructOptionList(this.props.Login.masterData.SampleType || [], "nsampletypecode", "ssampletypename", undefined, undefined, true);
      const SampleType = SampleMap.get("OptionList");
      const SampleTypeValue = {
        nsampletypecode: SampleType.length > 0 ? SampleType[0] : ""
      };
      const RegMap = constructOptionList(this.props.Login.masterData.RegistrationType || [], "nregtypecode", "sregtypename", undefined, undefined, true);
      const RegistrationType = RegMap.get("OptionList");
      const RegTypeValue = {
        nregtypecode: RegistrationType.length > 0 ? RegistrationType[0] : ""
      };
      const RegSubMap = constructOptionList(this.props.Login.masterData.RegistrationSubType || [], "nregsubtypecode", "sregsubtypename", 'nsorter', "ascending", false);
      const RegistrationSubType = RegSubMap.get("OptionList"); //const regSubTypeValue = {nregsubtypecode: RegistrationSubType.length> 0? RegistrationSubType[2]: ""};

      const FilterStatus = constructOptionList(this.props.Login.masterData.FilterStatus || [], "napprovalstatuscode", "stransdisplaystatus", undefined, undefined, true);
      const FilterList = FilterStatus.get("OptionList");
      let FilterStatusValue = {};
      let regSubTypeValue = {};

      if (this.props.Login.masterData.operation === "FilterSubmit") {
        FilterStatusValue = this.state.FilterStatusValue;
        regSubTypeValue = this.state.regSubTypeValue;
      } else {
        FilterStatusValue = {
          ntransactionstatus: FilterList.length > 0 ? FilterList[1] : ""
        };
        regSubTypeValue = {
          nregsubtypecode: RegistrationSubType.length > 0 ? {
            label: this.props.Login.masterData.RegistrationSubTypeValue.sregsubtypename,
            value: this.props.Login.masterData.RegistrationSubTypeValue.nregsubtypecode,
            item: this.props.Login.masterData.ApprovalVersionValue
          } : ""
        };
      }

      let RealFilterValue = {};
      let RealRegSubTypeValue = {};
      let RealFromDate = {};
      let RealToDate = {};

      if (this.props.Login.masterData.RealFilterValue !== previousProps.Login.masterData) {
        RealFilterValue = this.props.Login.masterData.RealFilterValue ? this.props.Login.masterData.RealFilterValue : this.props.Login.masterData.FilterStatusValue ? this.props.Login.masterData.FilterStatusValue.stransdisplaystatus : "";
      }

      if (this.props.Login.masterData.RealRegSubTypeValue !== previousProps.Login.masterData) {
        RealRegSubTypeValue = this.props.Login.masterData.RealRegSubTypeValue ? this.props.Login.masterData.RealRegSubTypeValue : this.props.Login.masterData.RegistrationSubTypeValue ? this.props.Login.masterData.RegistrationSubTypeValue.sregsubtypename : ""; // RealRegSubTypeValue = this.props.Login.masterData.RealRegSubTypeValue ? this.props.Login.masterData.RealRegSubTypeValue :
        //     this.props.Login.masterData.RegistrationSubTypeValue ? this.props.Login.masterData.RegistrationSubTypeValue.sregsubtypename : ""
      }

      if (this.props.Login.masterData.RealFromDate !== previousProps.Login.masterData) {
        RealFromDate = this.props.Login.masterData.RealFromDate ? this.props.Login.masterData.RealFromDate : this.props.Login.masterData.RealFromDate ? this.props.Login.masterData.RealFromDate : "";
      }

      if (this.props.Login.masterData.RealToDate !== previousProps.Login.masterData) {
        RealToDate = this.props.Login.masterData.RealToDate ? this.props.Login.masterData.RealToDate : this.props.Login.masterData.RealToDate ? this.props.Login.masterData.RealToDate : "";
      } // if (this.props.Login.masterData.Registration !== previousProps.Login.masterData) {
      //     const  Registration  = this.props.Login.masterData;
      //     sortData(Registration, "descending", "nregcertificatecode");
      // }


      let {
        printHistoryDataState,
        appHistoryDataState,
        resultsDataState
      } = this.state;
      let isStateChanged = false;

      if (this.props.Login.printHistoryDataState && this.props.Login.printHistoryDataState !== previousProps.Login.printHistoryDataState) {
        printHistoryDataState = this.props.Login.printHistoryDataState;
        isStateChanged = true;
      }

      if (this.props.Login.appHistoryDataState && this.props.Login.appHistoryDataState !== previousProps.Login.appHistoryDataState) {
        appHistoryDataState = this.props.Login.appHistoryDataState;
        isStateChanged = true;
      }

      if (this.props.Login.resultsDataState && this.props.Login.resultsDataState !== previousProps.Login.resultsDataState) {
        resultsDataState = this.props.Login.resultsDataState;
        isStateChanged = true;
      }

      const RealSampleTypeValue = this.props.Login.masterData.SampleTypeValue ? this.props.Login.masterData.SampleTypeValue.ssampletypename : "";
      const RealRegTypeValue = this.props.Login.masterData.RegistrationTypeValue ? this.props.Login.masterData.RegistrationTypeValue.sregtypename : "";

      if (isStateChanged) {
        this.setState({
          printHistoryDataState,
          appHistoryDataState,
          resultsDataState,
          SampleType,
          RegistrationType,
          RegistrationSubType,
          FilterList,
          SampleTypeValue,
          RegTypeValue,
          RealSampleTypeValue,
          regSubTypeValue,
          FilterStatusValue,
          RealRegTypeValue,
          RealRegSubTypeValue,
          RealFilterValue,
          RealFromDate,
          RealToDate
        });
      } else {
        this.setState({
          SampleType,
          RegistrationType,
          RegistrationSubType,
          FilterList,
          SampleTypeValue,
          RegTypeValue,
          RealSampleTypeValue,
          regSubTypeValue,
          FilterStatusValue,
          RealRegTypeValue,
          RealRegSubTypeValue,
          RealFilterValue,
          RealFromDate,
          RealToDate
        });
      }
    } // if (this.props.Login.masterData.RealFilterValue !== previousProps.Login.masterData)  {
    //     const RealFilterValue = this.props.Login.masterData.RealFilterValue ? this.props.Login.masterData.RealFilterValue:""
    //     this.setState({RealFilterValue});
    //  }
    //else{
    //     const RealFilterValue = this.props.Login.masterData.RealFilterValue ? this.props.Login.masterData.RealFilterValue:""
    //     //this.setState({RealFilterValue});
    // }this.state.regSubTypeValue


    if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      const userRoleControlRights = [];

      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item => userRoleControlRights.push(item.ncontrolcode));
      }

      const controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode);
      this.setState({
        userRoleControlRights,
        controlMap
      });
    }
  }

}

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  updateStore,
  getSampleCertTypeChange,
  filterColumnData,
  getSampleCertRegSubTypeChange,
  getTestResultDataHistory,
  getActiveSampleHistory,
  generateCertificateAction,
  sentCertificateAction,
  correctionCertificateAction,
  xmlExportAction,
  getWholeFilterStatusHistory
})(injectIntl(SampleCertificationHistory));

