import {DEFAULT_RETURN} from './LoginTypes';
import rsapi from '../rsapi';
import { toast } from 'react-toastify';
import {sortData,constructOptionList} from '../components/CommonScript';
import { initRequest } from './LoginAction';
import { intl } from '../components/App';
import Axios from 'axios';


export function getSiteDetail (Site, userInfo, masterData) {
    return function (dispatch) {   
    dispatch(initRequest(true));
    return rsapi.post("site/getSiteScreen", {nsitecode:Site.nsitecode, userinfo:userInfo})
   .then(response=>{      
        masterData = {...masterData, ...response.data};        
        sortData(masterData);
        dispatch({type: DEFAULT_RETURN, payload:{masterData, operation:null, modalName:undefined, 
             loading:false,dataState:undefined}});   
   })
   .catch(error=>{
        dispatch({type: DEFAULT_RETURN, payload: {loading:false}})
       if (error.response.status === 500){
           toast.error(error.message);
       } 
       else{               
           toast.warn(error.response.data);
       }  
  
   })
}}

export function getSiteCombo(screenName, operation, primaryKeyName,  masterData, userInfo, ncontrolCode) {
    return function(dispatch){ 
       let urlArray = [];
       if (operation === "create") {
        const timeZoneService = rsapi.post("timezone/getTimeZone");
           urlArray = [timeZoneService];
       }
       else {
        const timeZoneService = rsapi.post("timezone/getTimeZone");

           const SiteByID = rsapi.post("site/getActiveSiteById", { [primaryKeyName] :masterData.selectedSite[primaryKeyName], "userinfo": userInfo });
           urlArray = [timeZoneService,SiteByID];
       }
   
       dispatch(initRequest(true));
       Axios.all(urlArray)
           .then(response => {
   
               let timezone = [];
               let selectedRecord = {};
           const timezoneMap = constructOptionList(response[0].data || [], "ntimezonecode",
            "stimezoneid", undefined, undefined, false);

            const TimeZoneList = timezoneMap.get("OptionList");
               if (operation === "update") {
   
                selectedRecord = response[1].data;
               timezone.push({ "value": response[1].data["ntimezonecode"], "label": response[1].data["stimezoneid"] });
                
                
                 selectedRecord["ntimezonecode"] = timezone[0];
                
                   // edqmManufacturer.push({
                   //     label: response[1].data["sofficialmanufname"],
                   //     value: response[1].data["nofficialmanufcode"]
                   // });
               }
               dispatch({type: DEFAULT_RETURN, 
                   payload:{  
                    TimeZoneList, isOpen: true,  
                   selectedRecord: operation === "update" ? selectedRecord : {"ntransactionstatus": 1}, 
                   timezone: timezone ,
                   operation: operation,
                        screenName: screenName,
                       openModal : true, ncontrolCode: ncontrolCode, loading:false
               }});
           })
           .catch(error => {
               dispatch({type: DEFAULT_RETURN, payload: {loading:false}})
               if (error.response.status === 500) {
                   toast.error(intl.formatMessage({ id: error.message }));
               }
               else {
                   toast.warn(intl.formatMessage({ id: error.response.data }));
               }
           })
       }
   }
