  showSampleInfo() {
    this.setState({
      showSample: true,
      showTest: false
    });
  }

  showTestAndSample() {
    this.setState({
      showSample: !this.state.showSample,
      showTest: !this.state.showTest
    });
  }

  showTestDetails() {
    const updateInfo = {
      typeName: DEFAULT_RETURN,
      data: {
        showSample: !this.state.showSample,
        showTest: !this.state.showTest
      }
    };
    this.props.updateStore(updateInfo);
  }

  checkPreregisterAndQuarentine(sample) {
    return sample['jsondata'].ntransactionstatus === transactionStatus.PREREGISTER || sample['jsondata'].ntransactionstatus === transactionStatus.QUARANTINE;
  }

  checkPreregisterRecordQuarantine(sample) {
    return sample['jsondata'].ntransactionstatus === transactionStatus.PREREGISTER;
  }

  gridfillingColumn(data) {
    //  const tempArray = [];
    const temparray = data && data.map(option => {
      return {
        "idsName": option,
        "dataField": option,
        "width": "200px",
        "columnSize": "3"
      };
    });
    return temparray;
  }

  ComponentObjectAfterSave() {
    const testrequired = this.state.selectedRecord.ntransactionstatus;
    let objcomponent = this.state.selectComponent;
    delete objcomponent.nneedservice;

    if (testrequired === 3) {
      this.props.getTestfromDB(objcomponent, this.props.Login, 2);
    } else {
      const Component = this.props.Login.Component || [];
      let saveComponent = { ...objcomponent
      };
      saveComponent["slno"] = this.props.Login.Component ? Object.keys(this.props.Login.Component).length + 1 : 1;
      saveComponent["scomponentname"] = objcomponent["ncomponentcode"].label;
      saveComponent["sstoragelocationname"] = objcomponent["nstoragelocationcode"] ? objcomponent["nstoragelocationcode"].label : "";
      saveComponent["sstorageconditionname"] = objcomponent["nstorageconditioncode"] ? objcomponent["nstorageconditioncode"].label : "";
      saveComponent["ncomponentcode"] = objcomponent["ncomponentcode"] ? objcomponent["ncomponentcode"].value : -1;
      saveComponent["nstoragelocationcode"] = objcomponent["nstoragelocationcode"] ? objcomponent["nstoragelocationcode"].value : -1;
      saveComponent["nstorageconditioncode"] = objcomponent["nstorageconditioncode"] ? objcomponent["nstorageconditioncode"].value : -1; //objcomponent["dreceiveddate"] = formatDate(objcomponent["dreceiveddate"]);
      // saveComponent["dreceiveddate"] = formatInputDate(objcomponent["dreceiveddate"], false);
      // saveComponent["sreceiveddate"] = formatInputDateWithoutT(objcomponent["dreceiveddate"], false);

      const dreceiveddate = objcomponent["dreceiveddate"];
      saveComponent["dreceiveddate"] = dreceiveddate;
      saveComponent["sreceiveddate"] = convertDateTimetoString(dreceiveddate, this.props.Login.userInfo);
      saveComponent["nallottedspeccode"] = objcomponent["nallottedspeccode"] ? objcomponent["nallottedspeccode"] : -1;
      saveComponent["nplasmafilecode"] = objcomponent["nplasmafilecode"] ? objcomponent["nplasmafilecode"].value : -1;
      saveComponent["splasmafilenumber"] = objcomponent["nplasmafilecode"] ? objcomponent["nplasmafilecode"].label : "";
      saveComponent["ntzdmanufdate"] = objcomponent["ntzdmanufdate"] ? objcomponent["ntzdmanufdate"].value : -1;
      saveComponent["ntzdreceivedate"] = objcomponent["ntzdreceivedate"] ? objcomponent["ntzdreceivedate"].value : -1;
      saveComponent["stzdreceivedate"] = objcomponent["ntzdreceivedate"] ? objcomponent["ntzdreceivedate"].label : "";
      saveComponent["stzdmanufdate"] = objcomponent["ntzdmanufdate"] ? objcomponent["ntzdmanufdate"].label : "";
      Component.unshift(saveComponent);
      const updateInfo = {
        typeName: DEFAULT_RETURN,
        data: {
          Component,
          openChildModal: false,
          selectedComponent: saveComponent,
          SelectedTest: [],
          SelectedSource: [],
          selectComponent: {},
          showSaveContinue: false,
          loadComponent: false,
          parentPopUpSize: "xl"
        }
      };
      this.props.updateStore(updateInfo);
    }
  }

  constructDesign(list) {
    let newList = [];

    if (list.length > 0) {
      list.map(i => {
        newList.push({
          [designProperties.LABEL]: i,
          [designProperties.VALUE]: i
        });
      });
    }

    return newList;
  }

  componentDidUpdate(previousProps) {
    if (this.props.Login.columnList !== previousProps.Login.columnList) {
      this.setState({
        columnList: this.props.Login.columnList,
        childColumnList: this.props.Login.childColumnList,
        withoutCombocomponent: this.props.Login.withoutCombocomponent,
        comboComponents: this.props.Login.comboComponents
      });
    }

    if (this.props.Login.regparentSubSampleColumnList !== previousProps.Login.regparentSubSampleColumnList) {
      this.setState({
        regparentSubSampleColumnList: this.props.Login.regparentSubSampleColumnList,
        regSubSamplecomboComponents: this.props.Login.regSubSamplecomboComponents,
        regSubSamplewithoutCombocomponent: this.props.Login.regSubSamplewithoutCombocomponent
      });
    }

    if (this.props.Login.showSaveContinue !== previousProps.Login.showSaveContinue) {
      this.setState({
        showSaveContinue: this.props.Login.showSaveContinue
      });
    }

    if (this.props.Login !== previousProps.Login) {
      this.PrevoiusLoginData = previousProps;
    }

    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      this.setState({
        selectedRecord: this.props.Login.selectedRecord
      });
    } else if (this.props.Login.selectedPrinterData !== previousProps.Login.selectedPrinterData) {
      this.setState({
        selectedPrinterData: this.props.Login.selectedPrinterData
      });
    } else if (this.props.Login.loadFile !== previousProps.Login.loadFile && this.props.Login.loadFile === false) {
      this.setState({
        selectedFile: undefined
      });
    } else if (this.props.Login.selectedPrinterData !== previousProps.Login.selectedPrinterData) {
      this.setState({
        selectedPrinterData: this.props.Login.selectedPrinterData
      });
    } else if (this.props.Login.selectedFilter !== previousProps.Login.selectedFilter) {
      this.setState({
        selectedFilter: this.props.Login.selectedFilter
      });
    }

    if (this.props.Login.popUptestDataState && this.props.Login.popUptestDataState !== previousProps.Login.popUptestDataState) {
      this.setState({
        popUptestDataState: this.props.Login.popUptestDataState
      });
    }

    if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      const userRoleControlRights = [];

      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item => userRoleControlRights.push(item.ncontrolcode));
      }

      const controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode);
      this.setState({
        userRoleControlRights,
        controlMap
      });
    }

    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      let DynamicSampleColumns = this.state.DynamicSampleColumns;
      let DynamicSubSampleColumns = this.state.DynamicSubSampleColumns;
      let DynamicTestColumns = this.state.DynamicTestColumns;
      let DynamicGridItem = this.state.DynamicGridItem;
      let DynamicGridMoreField = this.state.DynamicGridMoreField;
      let SingleItem = this.state.SingleItem;
      let testMoreField = this.state.testMoreField;
      let testListColumns = this.state.testListColumns;
      let {
        skip,
        take,
        testskip,
        testtake,
        subsampleskip,
        subsampletake,
        testCommentDataState,
        resultDataState,
        sampleGridDataState,
        popUptestDataState
      } = this.state;

      if (this.props.Login.masterData.DynamicDesign && this.props.Login.masterData.DynamicDesign !== previousProps.Login.masterData.DynamicDesign) {
        const dynamicColumn = JSON.parse(this.props.Login.masterData.DynamicDesign.jsondata.value);
        DynamicSampleColumns = this.constructDesign(dynamicColumn.listitem ? dynamicColumn.listitem : []);
        DynamicSubSampleColumns = this.constructDesign(dynamicColumn.subsamplelistitem ? dynamicColumn.subsamplelistitem : []);
        DynamicTestColumns = this.constructDesign(dynamicColumn.testlistitem ? dynamicColumn.testlistitem : []);
        DynamicGridItem = dynamicColumn.griditem ? dynamicColumn.griditem : [];
        DynamicGridMoreField = dynamicColumn.gridmoreitem ? dynamicColumn.gridmoreitem : [];
        SingleItem = this.constructDesign(dynamicColumn.displayfields ? dynamicColumn.displayfields : []);
        testMoreField = dynamicColumn.testlistmoreitem ? dynamicColumn.testlistmoreitem : [];
        testListColumns = this.constructDesign(dynamicColumn.testlistitem ? dynamicColumn.testlistitem : []);
      }

      let showSample = this.props.Login.showSample === this.state.showTest || this.state.showSample;
      let showTest = showSample ? false : true;
      const sampleTypeMap = constructOptionList(this.props.Login.masterData.SampleType || [], "nsampletypecode", "ssampletypename", "nsorter", "ascending", false);
      const registrationTypeMap = constructOptionList(this.props.Login.masterData.RegistrationType || [], "nregtypecode", "sregtypename", "nsorter", "ascending", false);
      const registrationSubTypeMap = constructOptionList(this.props.Login.masterData.RegistrationSubType || [], "nregsubtypecode", "sregsubtypename", "nsorter", "ascending", false);
      const filterStatusMap = constructOptionList(this.props.Login.masterData.FilterStatus || [], "ntransactionstatus", "stransdisplaystatus", "nsorter", "ascending", false); // const stateSampleType = sampleTypeMap.get("OptionList");

      skip = this.props.Login.skip === undefined ? skip : this.props.Login.skip;
      take = this.props.Login.take || take;
      testskip = this.props.Login.testskip === undefined ? testskip : this.props.Login.testskip;
      testtake = this.props.Login.testtake || testtake;
      subsampleskip = this.props.Login.subsampleskip === undefined ? subsampleskip : this.props.Login.subsampleskip;
      subsampletake = this.props.Login.subsampletake || subsampletake;

      if (this.props.Login.resultDataState && this.props.Login.resultDataState !== previousProps.Login.resultDataState) {
        resultDataState = this.props.Login.resultDataState;
      }

      if (this.props.Login.testCommentDataState && this.props.Login.testCommentDataState !== previousProps.Login.testCommentDataState) {
        testCommentDataState = this.props.Login.testCommentDataState;
      }

      if (this.props.Login.sampleGridDataState && this.props.Login.sampleGridDataState !== previousProps.Login.sampleGridDataState) {
        sampleGridDataState = this.props.Login.sampleGridDataState;
      }

      if (this.props.Login.popUptestDataState && this.props.Login.popUptestDataState !== previousProps.Login.popUptestDataState) {
        popUptestDataState = this.props.Login.popUptestDataState;
      }

      this.setState({
        DynamicSampleColumns,
        DynamicSubSampleColumns,
        DynamicTestColumns,
        SingleItem,
        testMoreField,
        DynamicGridItem,
        DynamicGridMoreField,
        testListColumns,
        stateSampleType: sampleTypeMap.get("OptionList"),
        stateRegistrationType: registrationTypeMap.get("OptionList"),
        stateRegistrationSubType: registrationSubTypeMap.get("OptionList"),
        stateFilterStatus: filterStatusMap.get("OptionList"),
        popUptestDataState,
        showSample,
        showTest,
        skip,
        take,
        testskip,
        subsampleskip,
        subsampletake,
        testtake,
        testCommentDataState,
        resultDataState,
        sampleGridDataState
      });
    }
  }

  onUpdateRegistration(saveType, formRef, operation, flag) {
    const inputData = {
      userinfo: this.props.Login.userInfo
    };
    let initialParam = {
      nfilterstatus: this.props.Login.masterData.FilterStatusValue.ntransactionstatus,
      userinfo: this.props.Login.userInfo,
      nsampletypecode: this.props.Login.masterData.RealSampleTypeValue.nsampletypecode,
      nregtypecode: this.props.Login.masterData.RealRegTypeValue.nregtypecode,
      nregsubtypecode: this.props.Login.masterData.RealRegSubTypeValue.nregsubtypecode,
      fromdate: "",
      todate: "",
      nflag: 2,
      ntype: 1,
      npreregno: String(this.state.selectedRecord.npreregno)
    };
    inputData["initialparam"] = initialParam; // console.log("edit1:", this.props.Login.regRecordToEdit);

    inputData["samplebeforeedit"] = JSON.parse(JSON.stringify(this.props.Login.regRecordToEdit));
    inputData["registration"] = JSON.parse(JSON.stringify(this.state.selectedRecord));
    inputData["flag"] = flag === undefined ? 1 : flag;
    Object.keys(this.state.selectedRecord).map(key => {
      if (typeof this.state.selectedRecord[key] === "object") {
        if (this.state.selectedRecord[key] === null) {
          return inputData["registration"][key] = this.state.selectedRecord[key];
        } else if (this.state.selectedRecord[key] instanceof Date) {
          return inputData["registration"][key] = formatInputDate(this.state.selectedRecord[key], false);
        } else {
          return inputData["registration"][key] = this.state.selectedRecord[key].value;
        }
      } else {
        return inputData["registration"][key] = this.state.selectedRecord[key];
      }
    }); // console.log("edit:", inputData);
    // if (inputData["registration"] ) {
    //     delete inputData["registration"]["esignpassword"]
    //     delete inputData["registration"]["esigncomments"]
    //     delete inputData["registration"]["agree"]
    // }

    const inputParam = {
      classUrl: this.props.Login.inputParam.classUrl,
      methodUrl: "Registration",
      displayName: this.props.Login.inputParam.displayName,
      inputData: inputData,
      operation: operation,
      saveType,
      formRef,
      action: 'editSample',
      showConfirmAlert: false,
      resultDataState: this.state.resultDataState,
      testCommentDataState: this.state.testCommentDataState // dataState:undefined, selectedId

    };

    if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, this.props.Login.ncontrolCode)) {
      const updateInfo = {
        typeName: DEFAULT_RETURN,
        data: {
          loadEsign: true,
          screenData: {
            inputParam,
            masterData: this.props.Login.masterData
          },
          saveType,
          parentPopUpSize: "lg"
        }
      };
      this.props.updateStore(updateInfo);
    } else {
      //this.props.crudMaster(inputParam, this.props.Login.masterData, "openModal");
      this.props.updateRegistration(inputParam, this.props.Login.masterData, "openModal");
    }
  }

}

const mapStateToProps = state => {
  return {
    Login: state.Login
  };
};

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  updateStore,
  getSampleTypeChange,
  getRegTypeChange,
  getRegSubTypeChange,
  getAttachmentCombo,
  viewAttachment,
  getCommentsCombo,
  filterTransactionList,
  validateEsignCredential,
  getSampleChildTabDetail,
  getTestChildTabDetail,
  ReloadData,
  showUnderDevelopment,
  getPreviewTemplate,
  getChildValues,
  getRegistrationSample,
  getRegistrationsubSampleDetail,
  getRegistrationTestDetail,
  acceptRegistration,
  addMoreTest,
  createRegistrationTest,
