                // Map['ntransactionstatus'] = dataitem.ntransactionstatus;
                Map["ntransactiontestcode"] = acceptList.map(x => x.ntransactiontestcode).join(",");
                //  Map["ninserttransactionsamplecode"] = dataitem.ntransactionsamplecode
