import React from 'react';
import { Row, Col } from 'react-bootstrap';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { intl } from '../../components/App';
import { injectIntl } from 'react-intl';
import DateTimePicker from '../../components/date-time-picker/date-time-picker.component';
import { MediaHeader } from '../../components/App.styles';

const ResultEntryInstrumentForm = (props) => {
    return (
        <>
            {Object.values(props.selecteRecord).length > 0 ?
                <Row className="mb-4">
                    {props.needSubSample ?

                        <Col md={12}>
                            <MediaHeader className={`labelfont`}>{intl.formatMessage({ id: "IDS_SAMPLEARNO" })}: {" " + props.selecteRecord.ssamplearno}</MediaHeader>
                        </Col>
                        :
                        <Col md={12}>
                            <MediaHeader className={`labelfont`}>{intl.formatMessage({ id: "IDS_ARNO" })}: {" " + props.selecteRecord.sarno}</MediaHeader>
                        </Col>
                    }
                    <Col md={12}>
                        <MediaHeader className={`labelfont`}>{intl.formatMessage({ id: "IDS_TEST" })}: {" " + props.selecteRecord.stestsynonym}</MediaHeader>
                    </Col>
                </Row>
                : ""}

            <Row>
                <Col md={12}>
                    <FormSelectSearch
                        formLabel={intl.formatMessage({ id: "IDS_INSTRUMENTCATEGORY" })}
                        placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                        isSearchable={false}
                        name={"ninstrumentcatcode"}
                        isDisabled={false}
                        isMandatory={true}
                        options={props.instrumentcatValue || []}
                        optionId='ninstrumentcatcode'
                        optionValue='sinstrumentcatname'
                        value={props.selecteRecord ? props.selecteRecord.ninstrumentcatcode : ""}
                        showOption={true}
                        required={true}
                        onChange={(event) => props.onComboCategoryChange(event, 'ninstrumentcatcode')}
                        isMulti={false}
                        closeMenuOnSelect={true}
                    />
                </Col>

                <Col md={12}>
                    <FormSelectSearch
                        formLabel={intl.formatMessage({ id: "IDS_INSTRUMENTID" })}
                        placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                        isSearchable={false}
                        name={"ninstrumentcode"}
                        isDisabled={false}
                        isMandatory={true}
                        options={props.instrumentValue || []}
                        optionId='ninstrumentcode'
                        optionValue='sinstrumentid'
                        value={props.selecteRecord ? props.selecteRecord.ninstrumentcode : ""}
                        showOption={true}
                        required={true}
                        onChange={(event) => props.onComboChange(event, 'ninstrumentcode')}
                        isMulti={false}
                        closeMenuOnSelect={true}
                    />
                </Col>

                <Col md={6}>
                    <DateTimePicker
                        name={"dfromdate"}
                        label={intl.formatMessage({ id: "IDS_FROM" })}
                        className='form-control'
                        placeholderText="Select date.."
                        selected={props.selecteRecord["dfromdate"]}
                        value={props.selecteRecord["dfromdate"]}
                        //dateFormat={"dd/MM/yyyy HH:mm:ss"}
                        dateFormat={props.userInfo.ssitedatetime}
                        timeInputLabel={props.intl.formatMessage({ id: "IDS_TIME" })}
                        showTimeInput={true}
                        isClearable={false}
                        isMandatory={true}
                        required={true}
                        onChange={date => props.onDateChange("dfromdate", date)}

                    />
                </Col>
                <Col md={6}>
                    <FormSelectSearch
                        name={"ntzfromdate"}
                        formLabel={intl.formatMessage({ id: "IDS_TIMEZONE" })}
                        placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                        options={props.timeZoneListData}
                        optionId="ntimezonecode"
                        optionValue="stimezoneid"
                        value={props.selecteRecord.ntzfromdate}
                        defaultValue={props.selecteRecord.ntzfromdate}
                        isMandatory={true}
                        isMulti={false}
                        isSearchable={true}
                        isClearable={false}
                        isDisabled={false}
                        closeMenuOnSelect={true}
                        alphabeticalSort={true}
                        onChange={(event) => props.onComboChange(event, 'ntzfromdate')}
                    />
                </Col>
                <Col md={6}>
                    <DateTimePicker
                        name={"dtodate"}
                        label={intl.formatMessage({ id: "IDS_TO" })}
                        className='form-control'
                        placeholderText="Select date.."
                        selected={props.selecteRecord["dtodate"]}
                        value={props.selecteRecord["dtodate"]}
                        //  dateFormat={"dd/MM/yyyy HH:mm:ss"}
                        dateFormat={props.userInfo.ssitedatetime}
                        timeInputLabel={props.intl.formatMessage({ id: "IDS_TIME" })}
                        showTimeInput={true}
                        isClearable={false}
                        isMandatory={true}
                        required={true}
                        onChange={date => props.onDateChange("dtodate", date)}

                    />
                </Col>
                <Col md={6}>
                    <FormSelectSearch
                        name={"ntztodate"}
                        formLabel={intl.formatMessage({ id: "IDS_TIMEZONE" })}
                        placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                        options={props.timeZoneListData}
                        optionId="ntimezonecode"
                        optionValue="stimezoneid"
                        value={props.selecteRecord.ntztodate}
                        defaultValue={props.selecteRecord.ntztodate}
                        isMandatory={true}
                        isMulti={false}
                        isSearchable={true}
                        isClearable={false}
                        isDisabled={false}
                        closeMenuOnSelect={true}
                        alphabeticalSort={true}
                        onChange={(event) => props.onComboChange(event, 'ntztodate')}
                    />
                </Col>
            </Row>
        </>
    )
}
export default injectIntl(ResultEntryInstrumentForm);
