import { toast } from "react-toastify";
import { sortData } from "../components/CommonScript";
import rsapi from "../rsapi";
import { initRequest } from "./LoginAction";
import { DEFAULT_RETURN } from "./LoginTypes";

export function getSubSampleBySample (methodParam) {            
    return function (dispatch) {  
    dispatch(initRequest(true));
    return rsapi.post("coarelease/getSubSampleBySample", 
                                    {nsitecode:methodParam.nsitecode, nusercode:methodParam.primaryKeyValue,
                                    userinfo:methodParam.userInfo})
    .then(response=>{ 
            let releaseSubSample =response.data["RS_SubSample"];           
           // releaseSubSample.set(parseInt(Object.keys(response.data["RS_SubSample"])[0]), Object.values(response.data["RS_SubSample"])[0]);
            const masterData = methodParam.masterData;
            sortData(masterData);
            dispatch({type: DEFAULT_RETURN, payload:{masterData,
                        data: methodParam.data, releaseSubSample,
                        dataState:methodParam.dataState,
                        loading:false
                    }});
        })
        .catch(error=>{
            dispatch({type: DEFAULT_RETURN, payload: {loading:false}})
            if (error.response.status === 500){
                toast.error(error.message);
            } 
            else{               
                toast.warn(error.response.data);
            }  
        })        
    }
}
