        const allFieldExpanded = this.state.allFieldExpanded;
        let showMore = {};
        if (!allFieldExpanded) {
            showMore = masterData.map((master, index) => {
                return showMore = { ...showMore, [index]: true };
            });
        }
        this.setState({ showMore, allFieldExpanded: !allFieldExpanded });
