    mergeCells: mergeCells,
    splitCell: splitCell,
    splitCellWithType: splitCellWithType,
    setCellAttr: setCellAttr,
    toggleHeader: toggleHeader,
    toggleHeaderRow: toggleHeaderRow,
    toggleHeaderColumn: toggleHeaderColumn,
