import styled from 'styled-components';
export const CustomAlert = styled.div`
    width: 24rem;
`;
export const CustomAlertFooter = styled.div`
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: flex-end;
    padding: .75rem;
    border-top: 1px solid #dee2e6;

`;
