import * as React from 'react';
import { DropDownList } from '@progress/kendo-react-dropdowns';
//import { SchedulerEditItem } from '@progress/kendo-react-scheduler';
//import { Popup } from "@progress/kendo-react-popup";
import { connect } from 'react-redux';
import {getInstrumentForSchedule} from '../../actions'
//import { FormWithCustomEditor } from './custom-form';
const mapStateToProps = state => {
  return ({ Login: state.Login })
}

 const InstrumentCategory = props => {
  const handleChange = event => {
    if (props.onChange) {
      props.onChange.call(undefined, {
        value: event.value.value
      });
      props.getInstrumentForSchedule(event.value.value,props.Login.userInfo);

    }
  };
  return <DropDownList onChange={handleChange} value={props.instrumentCat.find(p => p.value === props.value)} data={props.instrumentCat} dataItemKey={'value'} textField={'label'} />;
};

export default connect(mapStateToProps,{getInstrumentForSchedule})(InstrumentCategory);


export const Instrument = props => {
  const handleChange = event => {
    if (props.onChange) {
      props.onChange.call(undefined, {
        value: event.value.value
      });
    }
  };
  return <DropDownList onChange={handleChange} value={props.instrument.find(t => t.value === props.value)} data={props.instrument} dataItemKey={'value'} textField={'label'} />;
};
