import * as React from 'react';
import { SchedulerForm } from '@progress/kendo-react-scheduler';
import  CustomFormEditor  from './custom-form-editor';
import { CustomDialog } from './custom-dialog';
import {intl} from '../../components/App'

export const FormWithCustomEditor = props => {
  const requiredValidator = React.useCallback(value => value&&value !== -1 ? intl.formatMessage({ids:"IDS_FIELDISREQUIRED."})  : undefined, []);
  const formValidator = (_dataItem, formValueGetter) => {
    let result = {};
    if(_dataItem.InstrumentCategory&&_dataItem.InstrumentCategory!==-1){
      result.Instrument = _dataItem.Instrument?undefined:[requiredValidator(_dataItem.InstrumentCategory)];
    }

    return result;
  };
  return <SchedulerForm    {...props} editor={CustomFormEditor} dialog={CustomDialog} validator={formValidator}
   />;
};
