// RegistrationTypeList=this.props.Login.masterData.RegistrationTypeList
// RegistrationSubTypeList=this.props.Login.masterData.RegistrationSubTypeList
var _RegistrationTypeListMap=constructOptionList(this.props.Login.masterData.registrationType||[],"nregtypecode","sregtypename",'nsorter','ascending',false);var _RegistrationSubTypeListMap=constructOptionList(this.props.Login.masterData.registrationSubType||[],"nregsubtypecode","sregsubtypename",'nsorter','ascending',false);var _UserRoleTemplateListMap=constructOptionList(this.props.Login.masterData.treeVersionTemplate||[],"ntreeversiontempcode","sversiondescription",'ntransactionstatus','descending',false);RegistrationTypeList=_RegistrationTypeListMap.get("OptionList");RegistrationSubTypeList=_RegistrationSubTypeListMap.get("OptionList");UserRoleTemplateList=_UserRoleTemplateListMap.get("OptionList");updateState=true;// if (this.props.dataState === undefined) {
