                    onHide={this.props.closeModal}
                    dialogClassName={`${this.state.nflag && this.state.nflag === 2 ? 
                        'alert-popup' : ''} modal-fullscreen`}
                    backdrop="static"
                    keyboard={false}
                    enforceFocus={false}
                    aria-labelledby="example-custom-modal-styling-title"
