                    <Modal.Header className="d-flex align-items-center">
                        <Modal.Title id="add" className="header-primary flex-grow-1">
                            {`${this.props.Login.operation === "update" ? this.props.intl.formatMessage({ id: "IDS_EDIT" }) : this.props.intl.formatMessage({ id: "IDS_ADD" })} ${this.props.intl.formatMessage({ id: "IDS_DESIGNTEMPLATE" })}`}
                        </Modal.Title>
                        <>
                            <Button className="btn-user btn-cancel" variant="" onClick={this.props.closeModal}>
                                <FormattedMessage id='IDS_CANCEL' defaultMessage='Cancel' />
                            </Button>
                            <Button className=" btn-user btn-primary-blue" onClick={() => this.handleSaveClick()}>
                                <FontAwesomeIcon icon={faSave} /> { }
                                <FormattedMessage id={"IDS_SAVE"} defaultMessage={"Save"} />
                            </Button>
                        </>
                    </Modal.Header>
                    <Modal.Body>
                        <div className="modal-inner-content">
                            <Row>
                                <Col md={12}>
                                   <DynamicSlideout
                                        selectedRecord={this.props.selectedRecord}
                                        templateData={this.props.templateData}
                                        handleChange={this.props.handleChange}
                                        handleDateChange={this.props.handleDateChange}
                                        onInputOnChange={this.onInputOnChange}
                                        onNumericInputChange={this.onNumericInputChange}
                                        comboData={this.props.comboData}
                                        onComboChange={this.onComboChange}
                                        userInfo={this.props.userInfo}
                                        timeZoneList={this.props.timeZoneList}
                                        defaultTimeZone={this.props.defaultTimeZone}
                                    /> 
                                </Col>
                            </Row>
                        </div>
                    </Modal.Body>
