                        : component.mandatory === true ?   data.push( { "mandatory": true, "idsName": component.label, "dataField": component.label, "mandatoryLabel": component.inputtype==="combo"?"IDS_SELECT":"IDS_ENTER", "controlType":  component.inputtype==="combo"?"selectbox":"textbox" }) : ""
                })
            })
        })
        const failedControls = [];
        const startLabel = [];
        let label = "IDS_ENTER";
        data.forEach(item => {
            if (this.state.selectedRecord[item.dataField] === undefined || this.state.selectedRecord[item.dataField] === null) {
                const alertMessage = (item.alertPreFix ? item.alertPreFix + " " : '') + this.props.intl.formatMessage({ id: item.idsName }) + (item.alertSuffix ? " " + item.alertSuffix : '')
                failedControls.push(alertMessage);
                startLabel.push(item.mandatoryLabel)//"IDS_PROVIDE";
            }
            else {
                if (item.validateFunction) {
                    const validateData = item.validateFunction;
                    if (validateData(this.state.selectedRecord[item.dataField]) === false) {
                        const alertMessage = (item.alertPreFix ? item.alertPreFix + " " : '') + this.props.intl.formatMessage({ id: item.idsName }) + (item.alertSuffix ? " " + item.alertSuffix : '')
                        failedControls.push(alertMessage);
                        startLabel.push(item.mandatoryLabel)
                    }
                }
                else {
                    if (typeof this.state.selectedRecord[item.dataField] === "object") {
                        //to validate FormSelectSearch component
                        if (this.state.selectedRecord[item.dataField].length === 0) {
                            const alertMessage = (item.alertPreFix ? item.alertPreFix + " " : '') + this.props.intl.formatMessage({ id: item.idsName }) + (item.alertSuffix ? " " + item.alertSuffix : '')
                            failedControls.push(alertMessage);
                            startLabel.push(item.mandatoryLabel)//"IDS_SELECT";
                        }
                    }
                    else if (typeof this.state.selectedRecord[item.dataField] === "string") {
                        //to handle string field -- added trim function
                        if (this.state.selectedRecord[item.dataField].trim().length === 0) {
                            const alertMessage = (item.alertPreFix ? item.alertPreFix + " " : '') + this.props.intl.formatMessage({ id: item.idsName }) + (item.alertSuffix ? " " + item.alertSuffix : '')
                            failedControls.push(alertMessage);
                            startLabel.push(item.mandatoryLabel)
                        }
                    }
                    else {
                        //number field
                        if (this.state.selectedRecord[item.dataField].length === 0) {
                            const alertMessage = (item.alertPreFix ? item.alertPreFix + " " : '') + this.props.intl.formatMessage({ id: item.idsName }) + (item.alertSuffix ? " " + item.alertSuffix : '')
                            failedControls.push(alertMessage);
                            startLabel.push(item.mandatoryLabel)
                        }
                    }
                }
            }
        });

        if (failedControls.length === 0) {

            let inputData = [];
            let selectedId = null;
            inputData["userinfo"] = this.props.Login.userInfo;
            let dataState = undefined;
            if (this.props.Login.operation === "update") {
                // edit
                selectedId = this.state.selectedRecord.nchecklistqbcode
                dataState = this.state.dataState
                inputData["registration"] = {
                    nreactregtemplatecode: this.props.Login.masterData.selectedTemplate.nreactregtemplatecode,
                    nsampletypecode: this.props.nsampletypecode,
                    sregtemplatename: this.state.selectedRecord.templateName,
                    jsonString: JSON.stringify(this.state.design),
                    ntransactionstatus: transactionStatus.DRAFT
                }
                const inputParam = {
                    methodUrl: 'RegistrationTemplate',
                    classUrl: 'dynamicpreregdesign',
                    displayName: this.props.Login.inputParam.displayName ? this.props.Login.inputParam.displayName : '',
                    inputData: inputData,
                    operation: this.props.Login.operation,
                    saveType, dataState, selectedId
                }
                const masterData = this.props.Login.masterData;
            }
            else {
                //add               
                inputData["registrationtemplate"] =
                {
                    nsampletypecode: this.props.nsampletypecode,
                    sregtemplatename: this.state.selectedRecord.templateName,
                    jsonString: JSON.stringify(this.state.design),
                    ntransactionstatus: transactionStatus.DRAFT
                };
            }
            // if (showEsign(this.props.Login.userRoleControlRights, 
            //     this.props.Login.userInfo.nformcode, this.props.Login.ncontrolcode)) {
            //     const updateInfo = {
            //         typeName: DEFAULT_RETURN,
            //         data: {
            //             loadEsign: true, screenData: { inputParam, masterData }, saveType
            //         }
            //     }
            //     this.props.updateStore(updateInfo);
            // }
            // else {
                let Map={}
                Map["FromDate"] = rearrangeDateFormat(this.props.Login.userInfo, this.props.fromDate);//formatDate(this.fromDate);
                Map["ToDate"] = rearrangeDateFormat(this.props.Login.userInfo, this.props.toDate);//formatDate(this.toDate);
                Map["nregtypecode"] = this.props.Login.masterData.RealRegTypeValue.nregtypecode;
                Map["nregsubtypecode"] = this.props.Login.masterData.RealRegSubTypeValue.nregsubtypecode;
                Map["nsampletypecode"] = this.props.Login.masterData.RealSampleTypeValue.nsampletypecode;
                Map["nfilterstatus"] = transactionStatus.PREREGISTER;
                this.props.preRegAction(saveType, 
                    this.state.selectedRecord,
                    this.props.comboComponents,
                    this.props.Login.userInfo,Map);
            // }
                //this.props.onSaveClick(saveType, this.formRef);
        }
        else {
            label = startLabel[0] === undefined ? label : startLabel[0];
            toast.info(`${this.props.intl.formatMessage({ id: label })} ${failedControls[0]}`);
        }
    }
}
