      "idsName": "IDS_OLDPASSWORD",
      "dataField": "soldpassword",
      "mandatory": false
    }, {
      "idsName": "IDS_NEWPASSWORD",
      "dataField": "snewpassword",
      "mandatory": false
    }, {
      "idsName": "IDS_CONFIRMPASSWORD",
      "dataField": "sconfirmpassword",
      "mandatory": true
    }] : mandatoryField = [];
    ;
    return mandatoryField;
  }

  componentDidUpdate(prevProps) {
    //   if (this.props.Login.isDeputyLogin === true) {
    //   if (this.props.Login.navigation === "home") {
    //      this.props.history.push('/');
    //  }
    // }
    if (this.props.Login.createPwdRecord !== prevProps.Login.createPwdRecord) {
      this.setState({
        createPwdRecord: this.props.Login.createPwdRecord
