    onKeyPress = (event, index, paremterResultcode) => {
        if (event.keyCode === 13) {
            for (let i = 0; i < event.target.form.elements.length; i++) {
                if (parseInt(event.target.form.elements[i].id) === paremterResultcode[index + 1]) {
                    event.target.form.elements[i].focus();
