                    let childData = childArray[i]
                    if (clickedItem ? (clickedItem.id === childData.id) : (childData.editable === true)) {
                        return treeData;
                    } else {
                        if (childData.items) {
                            ParentItem = this.searchClickedItemParent(childData, clickedItem)
                            if (ParentItem) {
