                                                //         },
                                                //         {
                                                //             title: this.props.intl.formatMessage({ id: "IDS_DELETE" }),
                                                //             controlname: "faTrashAlt",
                                                //             objectName: "mastertodelete",
                                                //             hidden: this.state.userRoleControlRights.indexOf(deleteId) === -1,
