import { initRequest } from './LoginAction';
import rsapi from '../rsapi';
import { DEFAULT_RETURN } from './LoginTypes';
import { toast } from 'react-toastify';
import { openModal } from './ApprovalConfigAction';

//	ALPD-5322	Added action file by Vishakh for Out Of Specification screen
export function getOOSMasterData(inputParam) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("oos/getOOS", inputParam.inputData)
            .then(response => {
                let responseData = response.data;
                let masterData = {
                    ...inputParam.masterData,
                    ...responseData,
                    searchedData: undefined
                }
                dispatch(
                    {
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            loading: false
                        }
                    }
                )
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            }
            )
    }
}

export const getOosMasterDetail = (OosMasterItem, userInfo, masterData) => {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("oos/getActiveOosMasterById", { noosmastercode: OosMasterItem.noosmastercode, userinfo: userInfo })
            .then(response => {
                masterData = { ...masterData, ...response.data };
                dispatch(
                    {
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            loading: false
                        }
                    }
                )
            })
            .catch(error => {
                dispatch({
                    type: DEFAULT_RETURN,
                    payload: {
                        loading: false
                    }
                });
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            });
    }
}

export function onSaveOOSWorkFlowTransaction(inputParam) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post(inputParam.inputData.classUrl + "/" + inputParam.inputData.operation + inputParam.inputData.methodUrl, inputParam.inputData.formData)
            .then(response => {
                const masterData = {
                    ...inputParam.inputData.masterData,
                    ...response.data
                };
                dispatch(
                    {
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            loading: false,
                            openModal: false
                        }
                    }
                )
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response && error.response.status === 500) {
                    toast.error(error.message);
                }
                else if (error.response === undefined) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}

export function onUpdateOOSWorkFlowTransaction(inputParam) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post(inputParam.classUrl + "/" + inputParam.operation + inputParam.methodUrl, inputParam.inputData)
            .then(response => {
                const masterData = {
                    ...inputParam.masterData,
                    ...response.data
                };
                dispatch(
                    {
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            loading: false,
                            openModal: false,
                            selectedId: ""
                        }
                    }
                )
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response && error.response.status === 500) {
                    toast.error(error.message);
                }
                else if (error.response === undefined) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}

