export function onSaveOOSChecklistQb(inputParam) {
    return function (dispatch) {
        let listResultCheckList = [];
        if (inputParam.selectedRecord && inputParam.selectedRecord.jsondata) {
            inputParam.selectedRecord.editedQB.map(qbcode =>
                listResultCheckList.push(inputParam.selectedRecord[qbcode]))
            let inputParamData = {
                noosworkflowtransactioncode: inputParam.inputData.noosworkflowtransactioncode,
                OOSChecklistQb: {
                    jsondata: inputParam.selectedRecord.jsondata,
                    noosworkflowtransactioncode: inputParam.inputData.noosworkflowtransactioncode,
                },
                userinfo: inputParam.inputData.userinfo,
                ncontrolcode: -1
            }
            dispatch(initRequest(true));
            rsapi.post("oos/createOOSChecklistQb", inputParamData)
                .then(response => {
                    dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                            selectedRecord: {},
                            selectedId: "",
                            templateData: undefined,
                            openModal: false,
                            loading: false,
                            loadEsign: false
                        }
                    })
                })
                .catch(error => {
                    dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                            loading: false,
                            loadEsign: false
                        }
                    })
                    if (error.response.status === 500) {
                        toast.error(error.message);
                    } else {
                        toast.warn(error.response.data);
                    }
                })
        } else {
            dispatch({
                type: DEFAULT_RETURN,
                payload: {
                    openTemplateModal: false,
                    selectedRecord: {},
                    loading: false,
                    loadEsign: false
                }
            })
        }
    }
}

export function deleteOOSWorkFlowTransaction(inputParam) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post(inputParam.classUrl + "/" + inputParam.operation + inputParam.methodUrl, inputParam.inputData)
            .then(response => {
                const masterData = {
                    ...inputParam.masterData,
                    ...response.data
                };
                dispatch(
                    {
                        type: DEFAULT_RETURN, payload: {
                            masterData,
                            loading: false,
                            openModal: false
                        }
                    }
                )
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response && error.response.status === 500) {
                    toast.error(error.message);
                }
                else if (error.response === undefined) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}

