import React from 'react';
import { Col, Row, Form, Button } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
import { connect } from 'react-redux';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { transactionStatus } from '../../components/Enumeration';
import DateTimePicker from '../../components/date-time-picker/date-time-picker.component';
import FormInput from '../../components/form-input/form-input.component';
import { templateChangeHandler } from '../checklist/checklist/checklistMethods';

const mapStateToProps = state => {
    return ({ Login: state.Login })
}

class AddOOSChecklistQb extends React.Component {
    constructor(props) {
        super(props)
        const dataState = {
            skip: 0,
            take: this.props.settings ? parseInt(this.props.settings[14]) : 5,
        };
        this.state = {
            loading: false,
            dataState: dataState,
            openModal: false,
            inputParam: this.props.inputParam,
            loadEsign: false,
            operation: this.props.operation ? this.props.operation : "",
            selectedRecord: {
                ...this.props.selectedRecord,
                noosworkflowtypecode: this.props.noosworkflowtypecode,
                ntranscode: this.props.ntranscode
            },
            templateData: this.props.templateData
        }
    }

    render() {
        return (
            <>
                <Row>
                    <Col>
                        {this.state.templateData.map(control =>
                            this.renderSwitch(control, this.state.selectedRecord)
                        )}
                    </Col>
                </Row>
            </>
        )
    }

    renderSwitch = (control, selectedRecord) => {
        switch (control.nchecklistcomponentcode) {
            case 1: {
                let comboData = control.squestiondata ? control.squestiondata.split(",") : []
                let options = comboData ? comboData.map(option => ({ "value": option, "label": option })) : []
                return (
                    <Col md={12}>
                        <FormSelectSearch
                            name={control.squestion}
                            as={"select"}
                            onChange={(event) => this.onTemplateComboChange(event, control)}
                            formLabel={control.squestion}
                            placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                            isMandatory={control.nmandatoryfield === transactionStatus.YES ? true : false}
                            defaultValue={control.sdefaultvalue ? { "value": control.sdefaultvalue, "label": control.sdefaultvalue } : ""}
                            options={options}
                            optionId={"value"}
                            optionValue={"label"}
                            isMulti={false}
                            isDisabled={false}
                            isSearchable={false}
                            isClearable={true}
                        />
                    </Col>
                );
            }
            case 2: {
                return (
                    <Col md={12}>
                        <FormInput
                            name={control.squestion}
                            label={control.squestion}
                            type="text"
                            defaultValue={control.sdefaultvalue ? control.sdefaultvalue : ""}
                            isMandatory={control.nmandatoryfield === transactionStatus.YES ? true : false}
                            required={control.nmandatoryfield === transactionStatus.YES ? true : false}
                            maxLength={100}
                            onChange={(event) => this.onTemplateInputChange(event, control)}
                        // id={control.nchecklistversionqbcode}
                        />
                    </Col>
                );
            }
            case 3: {
                return (
                    <Col md={12}>
                        <FormTextarea
                            label={control.squestion}
                            name={control.squestion}
                            onChange={(event) => this.onTemplateInputChange(event, control)}
                            defaultValue={control.sdefaultvalue ? control.sdefaultvalue : ""}
                            isMandatory={control.nmandatoryfield === transactionStatus.YES ? true : false}
                            required={control.nmandatoryfield === transactionStatus.YES ? true : false}
                            rows="2"
                            maxLength={255}
                        />
                    </Col>
                );
            }
            case 4: {
                let checkboxes = control.squestiondata ? control.squestiondata.split(",") : []
                let defaultCheckboxes = control.sdefaultvalue ? control.sdefaultvalue.split(",") : []
                defaultCheckboxes = defaultCheckboxes.map(checkbox => checkbox.trim())
                return (
                    <Col md={12}>
                        <fieldset>
                            <Form.Group>
                                <Form.Label as="legend" htmlFor={control.squestion}>{control.squestion}{control.nmandatoryfield === transactionStatus.YES && <sup>*</sup>}</Form.Label>
                                {checkboxes.map(checkbox =>
                                    <Form.Check
                                        inline={true}
                                        type="checkbox"
                                        name={control.squestion}
                                        label={checkbox}
                                        onChange={(event) => this.onTemplateInputChange(event, control)}
                                        id={checkbox}
                                        defaultChecked={defaultCheckboxes ? defaultCheckboxes.includes(checkbox.trim()) ? true : false : false}
                                        isMandatory={control.nmandatoryfield === transactionStatus.YES ? true : false}
                                        required={control.nmandatoryfield === transactionStatus.YES ? true : false}
                                    />
                                )}
                            </Form.Group>
                        </fieldset>
                    </Col>

                );
            }
            case 5: {
                return (
                    <Col md={12}>
                        <Form.Group>
                            <Form.Label>{control.squestion}{control.nmandatoryfield === transactionStatus.YES && <sup>*</sup>}</Form.Label>{ }
                        </Form.Group>
                    </Col>
                );
            }
            case 6: {
                return (
                    <Col md={12}>
                        <Form.Group>
                            <Form.Label htmlFor={control.squestion}>{control.squestion}{control.nmandatoryfield === transactionStatus.YES && <sup>*</sup>}</Form.Label>
                            <Button className="btn" name={control.squestion}>
                                {control.squestion}
                            </Button>
                        </Form.Group>
                    </Col>
                );
            }
            case 7: {
                return (
                    <Col md={12}>
                        <DateTimePicker
                            name={control.squestion}
                            label={control.squestion}
                            placeholderText={"DD/MM/YYYY"}
                            // selected={
                            //     selectedRecord ?
                            //         selectedRecord[control.nchecklistversionqbcode] ? selectedRecord[control.nchecklistversionqbcode].sdefaultvalue :
                            //             control.sdefaultvalue ? new Date(control.sdefaultvalue) : null :
                            //         control.sdefaultvalue ? new Date(control.sdefaultvalue) : null
                            // }
                            selected={selectedRecord && selectedRecord[control.nchecklistversionqbcode] ? selectedRecord[control.nchecklistversionqbcode].sdefaultvalue
                                : new Date()}
                            isMandatory={control.nmandatoryfield === transactionStatus.YES ? true : false}
                            dateFormat="dd/MM/yyyy"
                            className='form-control'
                            onChange={(date) => this.onTemplateDateChange(date, control)}
                            isClearable={true}
                        />
                    </Col>
                );
            }
            case 8: {
                let radioButtons = control.squestiondata ? control.squestiondata.split(",") : []
                return (
                    <Col md={12}>
                        <fieldset>
                            <Form.Group>
                                <Form.Label as="legend" htmlFor={control.squestion}>{control.squestion}{control.nmandatoryfield === transactionStatus.YES && <sup>*</sup>}</Form.Label>
                                {radioButtons.map(radioButton =>
                                    <Form.Check
                                        inline={true}
                                        type="radio"
                                        name={control.squestion}
                                        label={radioButton}
                                        onChange={(event) => this.onTemplateInputChange(event, control)}
                                        id={radioButton}
                                        defaultChecked={control.sdefaultvalue ? control.sdefaultvalue.trim() === radioButton.trim() ? true : false : false}
                                        isMandatory={control.nmandatoryfield === transactionStatus.YES ? true : false}
                                        required={control.nmandatoryfield === transactionStatus.YES ? true : false}
                                    />
                                )}
                            </Form.Group>
                        </fieldset>
                    </Col>
                );
            }
            default:
                return ("");
        }
    }

    onTemplateInputChange = (event, control) => {
        let selectedRecord = templateChangeHandler(1, this.state.selectedRecord, event, control)
        this.setState({ selectedRecord });
        this.props.childDataChange(selectedRecord);
    }

    onTemplateComboChange = (comboData, control) => {
        let selectedRecord = templateChangeHandler(2, this.state.selectedRecord, comboData, control)
        this.setState({ selectedRecord });
        this.props.childDataChange(selectedRecord);
    }

    onTemplateDateChange = (dateData, control) => {
        let selectedRecord = templateChangeHandler(3, this.state.selectedRecord, dateData, control)
        this.setState({ selectedRecord });
        this.props.childDataChange(selectedRecord);
    }
}

export default connect(mapStateToProps, {})(injectIntl(AddOOSChecklistQb));
