            // }
        }
        // const updateInfo = {
        //     typeName: DEFAULT_RETURN,
        //     data: {
        //         data: undefined, dataState: undefined,

        //     }
        // }
        // this.props.updateStore(updateInfo);
    }
    headerSelectionChange = (event) => {
        const checked = event.syntheticEvent.target.checked;
