                this.props.Login.masterData.ReleaseSample.map(item => {
                    if (event.dataItem.npreregno === item.npreregno) {
                        item.selected = checked;
                    }
                })
            }
            else {
                this.props.Login.masterData.ReleaseSample.map(item => {
                    if (event.dataItem.npreregno === item.npreregno) {
