            }
        }
        else {
            openModal = false;
            loadPrinter = false;
            loadPreregister = false;
            loadPoolSource = false;
            selectedRecord = {};
            openPortal = false;

            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    openModal, loadPreregister, selectedRecord,
                    screenName, insertSourcePreregno: undefined,
                    loadPrinter, loadPoolSource, openPortal,
