                if (this.props.Login.masterData.searchedData.length <= this.state.skip) {
                    this.setState({ skip: 0 });
                }
            }
        }
        if (bool) {
            this.setState({
                selectedRecord, comboitem, filterData, selectedcombo, filterCatList, selectedMaterialCat, selectedMaterialcombo,
