    /*  quotationData["quotation"]["nuserrolecode"] = this.state.selectedRecord["nuserrolecode"]!==""  ? this.state.selectedRecord["nuserrolecode"].value
        : transactionStatus.NA;

      quotationData["quotation"]["nusercode"] = this.state.selectedRecord["nusercode"]!==""  ? this.state.selectedRecord["nusercode"].value
        : transactionStatus.NA;  */

