  closeModal = () => {
    let loadEsign = this.props.Login.loadEsign;

    let openModal = this.props.Login.openModal;
    let selectedRecord = this.state.selectedRecord;
//    let selectedRecord = this.state.selectedRecord;
    if (this.props.Login.loadEsign) {
        if (this.props.Login.operation === "delete" || this.props.Login.operation === "approve") {
            loadEsign = false;
            openModal = false;
            selectedRecord = {};
        }
        else {
            loadEsign = false;
            selectedRecord['esignpassword'] = '';
            selectedRecord['esigncomments'] = '';
            selectedRecord['esignreason'] = '';
        } 
    }
    else {
        openModal = false;
        selectedRecord = {};
    }

    const updateInfo = {
        typeName: DEFAULT_RETURN,
        data: { openModal, loadEsign, selectedRecord }
    }
    this.props.updateStore(updateInfo);

}

  componentDidUpdate(previousProps) {
    let {
      selectedRecord,
      userRoleControlRights,
      controlMap,
      filterData,
      
    } = this.state;

    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      selectedRecord = this.props.Login.selectedRecord;
      this.setState({ selectedRecord });
    }
    if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      userRoleControlRights = [];
      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode
        ] &&
          Object.values(
            this.props.Login.userRoleControlRights[
            this.props.Login.userInfo.nformcode
            ]
          ).map((item) => userRoleControlRights.push(item.ncontrolcode));
      }
      controlMap = getControlMap(
        this.props.Login.userRoleControlRights,
        this.props.Login.userInfo.nformcode
      );
      this.setState({ controlMap, userRoleControlRights });
    }


    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      this.setState({
        selectedRecord
      });

      this.setState({ filterData });
    }
  }
  
  openFilter = () => {
    let showFilter = !this.props.Login.showFilter;
    const updateInfo = {
      typeName: DEFAULT_RETURN,
      data: { showFilter },
    };
    this.props.updateStore(updateInfo);
  };

  closeFilter = () => {
    const updateInfo = {
      typeName: DEFAULT_RETURN,
      data: { showFilter: false },
    };
    this.props.updateStore(updateInfo);
  };

  
}

export default connect(mapStateToProps, {
  callService,
  crudMaster,
  filterColumnData,
  updateStore,
  validateEsignCredential,
  getQuotation,getProjectTypeComboService,
  viewAttachment, addQuotation, getreloadQuotation, getQuotationAddTestService, getQuotationPricingEditService,
   GrossQuotation,getClientCategoryComboService,getQuotaionClientComboService,getProjectCodeComboService 
})(injectIntl(Quotation));
