    openModal = (screenName, ncontrolcode) => {
        if (this.state.breadCrumbData[1] && this.state.breadCrumbData[1].item && this.state.breadCrumbData[1].item.value) {
            const updateInfo = {
                typeName: DEFAULT_RETURN,
