            if (selectedRecord.ssampleformat.replaceAll("{","").replaceAll("}","").length <= 30) {
                this.setState({ selectedRecord });
            } else {
                toast.info(this.props.intl.formatMessage({ id: "IDS_EXCEEDSMAXCHARS" }));
            }

