    static getDerivedStateFromProps(props, state) {
        if (props.Login.masterStatus !== "") {
            toast.warn(props.Login.masterStatus);
            props.Login.masterStatus = "";
        }
        if (props.Login.multilingualMsg !== undefined && props.Login.multilingualMsg !== "") {
            toast.warn(props.intl.formatMessage({ id: props.Login.multilingualMsg }));
            props.Login.multilingualMsg = "";
        }
        if (props.Login.error !== state.error) {
            toast.error(props.Login.error)
            props.Login.error = "";
        }
        return null;
    }

    componentDidUpdate(previousProps) {
        let { userRoleControlRights, controlMap, selectedRecord, skip, take, masterData } = this.state;
        let bool = false;
        if (this.props.Login.masterData !== previousProps.Login.masterData) {
            if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
                userRoleControlRights = [];
                if (this.props.Login.userRoleControlRights) {
                    this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item =>
                        userRoleControlRights.push(item.ncontrolcode))
                }
                controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode)
                bool = true;
            }
        }
        if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
            selectedRecord = this.props.Login.selectedRecord
            bool = true;
        }
        if (this.props.Login.masterData !== previousProps.Login.masterData) {
            bool = true;
            skip = this.props.Login.skip === undefined ? skip : this.props.Login.skip
            take = this.props.Login.take || take
        }
        if (bool) {
            this.setState({
                userRoleControlRights, controlMap, selectedRecord, skip, take, masterData
            });
        }
    }

    shouldComponentUpdate(nextProps, nextState) {
        if (nextState.isInitialRender === false && (nextState.selectedRecord !== this.state.selectedRecord)) {
            return false;
        } else {
            return true;
        }
    }

    reloadData = () => {
        this.searchRef.current.value = "";
        let masterData = {
            ...this.props.Login.masterData
        };
        let inputData = {
            userinfo: this.props.Login.userInfo,
        }
        let inputParam = {
            masterData,
            inputData
        }
        this.props.getIncidentMasterData(inputParam);
    }

    closeModal = () => {
        let loadEsignStateHandle = this.props.Login.loadEsignStateHandle;
        let openModal = this.props.Login.openModal;
        let screenName = this.props.Login.screenName;
        let operation = this.props.Login.operation;
        let selectedRecord = this.props.Login.selectedRecord;
        let selectedId = this.props.Login.selectedId;
        let scomments = this.props.Login.scomments;
        let sadditionalcomments = this.props.Login.sadditionalcomments;
        if (this.props.Login.loadEsignStateHandle) {
            if (this.props.Login.operation === "close") {
                loadEsignStateHandle = false;
                openModal = false;
                selectedRecord = {};
            }
            else {
                loadEsignStateHandle = false;
                selectedRecord['esignpassword'] = ""
                selectedRecord['esigncomments'] = ""
                selectedRecord['esignreason'] = ""
            }
        }
        else {
            openModal = false;
            selectedRecord = {};
            screenName = "";
            operation = "";
            selectedId = "";
            scomments = "";
            sadditionalcomments = "";
        }
        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: { openModal, loadEsignStateHandle, selectedRecord, screenName, operation, selectedId, scomments, sadditionalcomments }
        }
        this.props.updateStore(updateInfo);
    }

    openIncidentModal = () => {
        let openModal = true;
        let screenName = "IDS_INCIDENT";
        let operation = "create";
        let selectedRecord = {};
        rsapi.post("timezone/getLocalTimeByZone", { userinfo: this.props.Login.userInfo })
            .then(response => {
                const currentTime = rearrangeDateFormat(this.props.Login.userInfo, response.data)
                const updateInfo = {
                    typeName: DEFAULT_RETURN,
                    data: { openModal, screenName, operation, selectedRecord, currentTime }
                }
                this.props.updateStore(updateInfo);
            })
    }

    dataStateChange = (event) => {
        this.setState({
            dataResult: process(this.props.Login.masterData.OosWorkFlowTransaction || [], event.dataState),
            dataState: event.dataState
        });
    }

    oosDetailsDataStateChange = (event) => {
        this.setState({
            dataResult: process(this.props.Login.masterData.OOSMasterDetails || [], event.dataState),
            oosDetailsDataState: event.dataState
        });
    }

    openIncidentStep = (controlCode) => {
        const isInitialRender = true;
        let selectedRecord = {};
        const url = "incident/getWorkFlowType";
        let urlArray = [];
        urlArray.push(rsapi.post(url, {
            userinfo: this.props.Login.userInfo,
            noosmastercode: (this.props.Login.masterData.selectedOosMaster && this.props.Login.masterData.selectedOosMaster.noosmastercode)
        }))
        Axios.all(urlArray)
            .then(response => {
                let responseData = response[0].data;
                const OosWorkFlowTypeMap = constructOptionList(responseData.oosWorkFlowType || [], "noosworkflowtypecode", "soosworkflowtypename", 'noosworkflowtypecode', 'ascending', false);
                let OosWorkFlowType = OosWorkFlowTypeMap.get("OptionList");
                let noosworkflowtypecode = [];
                let ntranscode = [];
                const OosStepStatusMap = constructOptionList(responseData.OosStepStatus || [], "ntranscode", "stransdisplaystatus", 'ntranscode', 'ascending', false);
                let OosStepStatus = OosStepStatusMap.get("OptionList");
                const updateInfo = {
                    typeName: DEFAULT_RETURN,
                    data: {
                        openModal: true,
                        OosWorkFlowType,
                        OosStepStatus,
                        noosworkflowtypecode,
                        ntranscode,
                        screenName: "IDS_INCIDENTWORKFLOWSTEP",
                        operation: "create",
                        isInitialRender,
