            masterData: masterData
        }
        if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, parameter.ncontrolCode)) {
            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    loadEsignStateHandle: true, screenData: { inputParam, masterData },
                    openModal: true, screenName: this.props.Login.screenName, operation: parameter.operation
                }
            }
            this.props.updateStore(updateInfo);
        } else {
            this.props.openCloseIncident(inputParam);
        }
    }

    getIncidentDetails = (incidentDetailParam) => {
        const masterData = incidentDetailParam.masterData;
        let inputData = {};
        inputData["noosmastercode"] = masterData && masterData.selectedOosMaster ? masterData.selectedOosMaster.noosmastercode : -1;
        inputData["userinfo"] = this.props.Login.userInfo;
        const inputParam = {
            classUrl: this.props.Login.inputParam.classUrl,
            methodUrl: "IncidentDetails",
            inputData: inputData,
            operation: "view",
            selectedRecord: this.state.selectedRecord,
            masterData: masterData,
            screenName: "IDS_INCIDENTDETAILS"
        }
        this.props.getIncidentDetailsData(inputParam);
    }

    validateEsign = () => {
        // let ncontrolcode = this.props.Login.ncontrolcode;
        let modalName = "openModal";
        const inputParam = {
            inputData: {
                "userinfo": {
                    ...this.props.Login.userInfo,
                    sreason: this.state.selectedRecord["esigncomments"],
                    nreasoncode: this.state.selectedRecord["esignreason"] && this.state.selectedRecord["esignreason"].value,
                    spredefinedreason: this.state.selectedRecord["esignreason"] && this.state.selectedRecord["esignreason"].label,
                },
                password: this.state.selectedRecord["esignpassword"]
            },
            screenData: this.props.Login.screenData
        }
        this.props.validateEsignforIncident(inputParam, modalName);
    }

