    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("trainingupdate/getCertifiedParticipants", { "userinfo": userInfo,  "ntrainingcode": parseInt(trainingUpdate.ntrainingcode) })
            .then(response => {

