import React from 'react';
import { Row, Col } from 'react-bootstrap';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { intl } from '../../components/App';
import { injectIntl } from 'react-intl';
import { MediaHeader } from '../../components/App.styles';
import FormInput from '../../components/form-input/form-input.component';
import FormTextarea from '../../components/form-textarea/form-textarea.component';

const ResultEntryMaterialForm = (props) => {
    return (
        <>
            {Object.values(props.selectedRecord).length > 0 ?
                <Row className="mb-4">
                    {props.needSubSample ?

                        <Col md={12}>
                            <MediaHeader className={`labelfont`}>{intl.formatMessage({ id: "IDS_SAMPLEARNO" })}: {" " + props.selectedRecord.ssamplearno}</MediaHeader>
                        </Col>
                        :
                        <Col md={12}>
                            <MediaHeader className={`labelfont`}>{intl.formatMessage({ id: "IDS_ARNO" })}: {" " + props.selectedRecord.sarno}</MediaHeader>
                        </Col>
                    }
                    <Col md={12}>
                        <MediaHeader className={`labelfont`}>{intl.formatMessage({ id: "IDS_TEST" })}: {" " + props.selectedRecord.stestsynonym}</MediaHeader>
                    </Col>
                </Row>
                : ""}

            <Row>
                <Col md={6}>
                    <Row>
                        <Col md={12}>
                            <FormSelectSearch
                                formLabel={intl.formatMessage({ id: "IDS_MATERIALTYPE" })}
                                placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                isSearchable={false}
                                name={"nmaterialtypecode"}
                                isDisabled={false}
                                isMandatory={true}
                                options={props.materialType || []}
                                value={props.selectedRecord ? props.selectedRecord.nmaterialtypecode : ""}
                                showOption={true}
                                required={true}
                                onChange={(event) => props.onMaterialComboChange(event, 'nmaterialtypecode')}
                                isMulti={false}
                                closeMenuOnSelect={true}
                            />
                            <FormSelectSearch
                                formLabel={intl.formatMessage({ id: "IDS_MATERIALCATEGORY" })}
                                placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                isSearchable={false}
                                name={"nmaterialcatcode"}
                                isDisabled={false}
                                isMandatory={true}
                                options={props.materialCategory || []}
                                value={props.selectedRecord ? props.selectedRecord.nmaterialcatcode : ""}
                                showOption={true}
                                required={true}
                                onChange={(event) => props.onMaterialComboChange(event, 'nmaterialcatcode')}
                                isMulti={false}
                                closeMenuOnSelect={true}
                            />
                            <FormSelectSearch
                                formLabel={intl.formatMessage({ id: "IDS_MATERIAL" })}
                                placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                isSearchable={false}
                                name={"nmaterialcode"}
                                isDisabled={false}
                                isMandatory={true}
                                options={props.material || []}
                                value={props.selectedRecord ? props.selectedRecord.nmaterialcode : ""}
                                showOption={true}
                                required={true}
                                onChange={(event) => props.onMaterialComboChange(event, 'nmaterialcode')}
                                isMulti={false}
                                closeMenuOnSelect={true}
                            />
                            <FormSelectSearch
                                formLabel={intl.formatMessage({ id: "IDS_MATERIALINVENTORY" })}
                                placeholder={intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                isSearchable={false}
                                name={"nmaterialinventorycode"}
                                isDisabled={false}
                                isMandatory={true}
                                options={props.materialInventory || []}
                                value={props.selectedRecord ? props.selectedRecord.nmaterialinventorycode : ""}
                                showOption={true}
                                required={true}
                                onChange={(event) => props.onMaterialComboChange(event, 'nmaterialinventorycode')}
                                isMulti={false}
                                closeMenuOnSelect={true}
                            />
                        </Col>
                    </Row>
                </Col>
                <Col md={6}>
                    <Row>
                        <Col md={12}>
                            <FormInput
                                label={props.intl.formatMessage({ id: "IDS_AVAILABLEQUANTITY" })}
                                name="savailablequantity"
                                type="text"
                                maxLength="100"
                                isMandatory={true}
                                value={props.selectedRecord["savailablequantity"] || []}
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_AVAILABLEQUANTITY" })}
                                isDisabled={true}
                            />
                        </Col>
                        <Col md={6}>
                            <FormInput
                                label={props.intl.formatMessage({ id: "IDS_USEDQTY" })}
                                name="susedquantity"
                                type="text"
                                maxLength="100"
                                isMandatory={true}
                                value={props.selectedRecord["susedquantity"] || []}
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_USEDQTY" })}
                            />
                        </Col>
                        <Col md={6}>
                            <FormInput
                                label={props.intl.formatMessage({ id: "IDS_UNIT" })}
                                name="sunitname"
                                type="text"
                                maxLength="100"
                                isMandatory={true}
                                value={props.selectedRecord["sunitname"] || []}
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_UNIT" })}
                                isDisabled={true}
                            />
                        </Col>
                        <Col md={12}>
                            <FormInput
                                label={props.intl.formatMessage({ id: "IDS_MOBILEPHASE" })}
                                name="smobilephase"
                                type="text"
                                maxLength="20"
                                value={props.selectedRecord["smobilephase"] || []}
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_MOBILEPHASE" })}
                            />
                            <FormInput
                                label={props.intl.formatMessage({ id: "IDS_CARRIERGAS" })}
                                name="scarriergas"
                                type="text"
                                maxLength="20"
                                value={props.selectedRecord["scarriergas"] || []}
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_CARRIERGAS" })}
                            />
                            <FormTextarea
                                label={props.intl.formatMessage({ id: "IDS_REMARKS" })}
                                name="sremarks"
                                type="text"
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_REMARKS" })}
                                value={props.selectedRecord ? props.selectedRecord["sremarks"] : ""}
                                isMandatory={false}
                                required={false}
                                maxLength={255}
                                row={2}
                            />
                        </Col>
                    </Row>
                </Col>
            </Row>
        </>
    )
}
export default injectIntl(ResultEntryMaterialForm);
