import React from 'react';
import './form-textarea.styles';
import { Form } from 'react-bootstrap';
const FormTextarea = ({
    name,
    type,
    placeholder,
    onChange,
    className,
    value,
    // error,
    errors,
    isMandatory,
    children,
    rows,
    label,
    defaultValue,
    readOnly,
    isDisabled,
    isInvalid,
    charLength,
    ...props
}) => (
    <React.Fragment>
        <Form.Group className="floating-label">
            <Form.Control as="textarea"
                id={name}
                name={name}
                placeholder={placeholder}
                rows={rows}
                onChange={onChange}
                value={value}
                className={className}
                defaultValue={defaultValue}
                maxLength={props.maxLength}
                required={props.required}
                readOnly={readOnly}
                disabled={isDisabled}
                autoComplete="off"
                isInvalid={isInvalid}
            />
            <Form.Label htmlFor={name}>{label}{isMandatory && <sup>*</sup>}</Form.Label>
            {props.showCharCount &&
                <aside style={{ fontSize: "small", float: "right" }}>
                    <span class="length">{charLength ? charLength : value ? value.length : 0}</span>
                    <span>/</span>
                    <span class="limit">{props.maxLength}</span>
                </aside>
            }
            <Form.Control.Feedback type="invalid">
                {errors}
            </Form.Control.Feedback>
        </Form.Group>
    </React.Fragment>
);

FormTextarea.defaultProps = {
    rows: "2",
    className: ""
}
export default FormTextarea;
