import React from 'react';
import {injectIntl } from 'react-intl';
import {Row, Col} from 'react-bootstrap';
import FormInput from '../../components/form-input/form-input.component';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import CustomSwitch from '../../components/custom-switch/custom-switch.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import DateTimePicker from '../../components/date-time-picker/date-time-picker.component';
import FormNumericInput from '../../components/form-numeric-input/form-numeric-input.component';
import {transactionStatus} from '../../components/Enumeration';

const AddGoodsIn = (props) =>{    
    
       return (
           <Row>                                
                <Col md={6}>
                    <Row>
                         <Col md={12}>
                         <FormSelectSearch
                                   name={"nmanufcode"}
                                   formLabel={ props.intl.formatMessage({ id:"IDS_MANUFACTURER"})}                                
                                   placeholder={ props.intl.formatMessage({ id:"IDS_SELECTRECORD"})}                              
                                   options={ props.manufacturerList}
                                   // optionId="nmanufcode"
                                   // optionValue="smanufname"
                                   value = { props.selectedRecord["nmanufcode"] || ""}
                                   isMandatory={true}
                                   isMulti={false}
                                   isSearchable={true}                               
                                   isDisabled={false}
                                   closeMenuOnSelect={true}
                                   //alphabeticalSort={true}
                                   onChange = {(event)=> props.onComboChange(event, 'nmanufcode')}  
                              />
                   
                         <FormSelectSearch
                                   name={"nrecipientcode"}
                                   formLabel={ props.intl.formatMessage({ id:"IDS_RECIPIENT"})}                                
                                   placeholder={ props.intl.formatMessage({ id:"IDS_SELECTRECORD"})}                                
                                   options={ props.recipientList}
                                   // optionId="nusercode"
                                   //optionValue="susername"
                                   value = { props.selectedRecord["nrecipientcode"] || ""}
                                   isMandatory={true}
                                   isMulti={false}
                                   isSearchable={true}                               
                                   isDisabled={false}
                                   closeMenuOnSelect={true}
                                  // alphabeticalSort={true}
                                   onChange = {(event)=> props.onComboChange(event, 'nrecipientcode')}  
                              />
                    
                         <FormInput
                              name={"sdeptname"}
                              type="text"
                              label={ props.intl.formatMessage({ id:"IDS_DEPARTMENT"})}                          
                              placeholder={ props.intl.formatMessage({ id:"IDS_DEPARTMENT"})}
                              value ={ props.selectedRecord["sdeptname"]}
                              isMandatory={true}
                              required={false}
                              maxLength={50}
                              readOnly={true}
                              onChange={(event)=> props.onInputOnChange(event)}
                         />
                                             
                         <FormNumericInput
                                   name={"nnoofpackages"}
                                   label={ props.intl.formatMessage({ id:"IDS_NOOFPACKAGES"})}                          
                                   // placeholder={ props.intl.formatMessage({ id:"IDS_NOOFPACKAGES"})}
                                   value ={ props.selectedRecord["nnoofpackages"]}
                                   type="number"                               
                                   strict={true}
                                   maxLength={9}
                                   noStyle={true}
                                   onChange={(event) => props.onNumericInputOnChange(event, "nnoofpackages")}
                                   precision={0}
                                   min={0}
                                   className="form-control"
                                   isMandatory={true}
                                   required={true}
                                   errors="Please provide a valid number."
                              />
                 
                              <FormSelectSearch
                                   name={"ncouriercode"}
                                   formLabel={ props.intl.formatMessage({ id:"IDS_COURIERCARRIER"})}                                
                                   placeholder={ props.intl.formatMessage({ id:"IDS_SELECTRECORD"})}                            
                                   options={ props.courierList}
                                   // optionId="ncouriercode"
                                   // optionValue="scouriername"
                                   value = { props.selectedRecord["ncouriercode"]||"" }
                                   isMandatory={false}
                                   isMulti={false}
                                   isSearchable={true}
                                   isClearable={true}                               
                                   isDisabled={false}
                                   closeMenuOnSelect={true}
                                   onChange = {(event)=> props.onComboChange(event, 'ncouriercode')}                               
                              />
                       
                              <FormInput
                                   name={"sconsignmentno"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_CONSIGNMENTNO"})}                  
                                   placeholder={ props.intl.formatMessage({ id:"IDS_CONSIGNMENTNO"})}
                                   value ={ props.selectedRecord["sconsignmentno"] || ""}
                                   isMandatory={false}
                                   required={false}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}
                                   />
                         </Col>                   
                    </Row>
               </Col>
               <Col md={6}>
                    <Row>
                         <Col md={12}>   
                              <DateTimePicker
                                        name={"dgoodsindate"} 
                                        label={ props.intl.formatMessage({ id:"IDS_GOODSINDATE"})}                     
                                        className='form-control'
                                        placeholderText="Select date.."
                                        selected={props.selectedRecord["dgoodsindate"]}
                                        dateFormat={props.userInfo.ssitedatetime}
                                        timeInputLabel=  {props.intl.formatMessage({ id:"IDS_TIME"})}
                                        showTimeInput={true}
                                        isClearable={false}
                                        isMandatory={true}                       
                                        required={true}
                                        maxDate={props.currentTime}
                                        maxTime={props.currentTime}
                                        onChange={date => props.handleDateChange("dgoodsindate", date)}
                                        value={props.selectedRecord["dgoodsindate"]}
                              />
                        
                              <FormSelectSearch
                                   name={"ntzgoodsindate"}
                                   formLabel={ props.intl.formatMessage({ id:"IDS_TIMEZONE"})}                                
                                   placeholder={ props.intl.formatMessage({ id:"IDS_SELECTRECORD"})}                               
                                   options={ props.timeZoneList}
                                   // optionId="ntimezonecode"
                                   // optionValue="stimezoneid"
                                   value = { props.selectedRecord["ntzgoodsindate"] }
                                   isMandatory={true}
                                   isMulti={false}
                                   isSearchable={true}
                                   isClearable={false}                               
                                   isDisabled={false}
                                   closeMenuOnSelect={true}
                                   //alphabeticalSort={true}
                                   onChange = {(event)=> props.onComboChange(event, 'ntzgoodsindate')}                               
                              />
                        
                              <CustomSwitch
                                   name={"noutofhours"}
                                   type="switch"
                                   label={ props.intl.formatMessage({ id:"IDS_OUTOFHOURS"})}
                                   placeholder={ props.intl.formatMessage({ id:"IDS_OUTOFHOURS"})}                            
                                   defaultValue ={ props.selectedRecord["noutofhours"] === transactionStatus.YES ? true :false }  
                                   isMandatory={false}                       
                                   required={false}
                                   checked={ props.selectedRecord["noutofhours"] === transactionStatus.YES ? true :false}
                                   onChange={(event)=> props.onInputOnChange(event)}
                                   />
                         
                              <FormInput
                                   name={"ssecurityrefno"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_SECURITYREFNO"})}                  
                                   placeholder={ props.intl.formatMessage({ id:"IDS_SECURITYREFNO"})}
                                   value ={ props.selectedRecord["ssecurityrefno"] || ""}
                                   isMandatory={props.selectedRecord["noutofhours"] === transactionStatus.YES ? true :false}
                                   required={props.selectedRecord["noutofhours"] === transactionStatus.YES ? true :false}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}
                                   />
                         
                              <FormTextarea
                                   name={"scomments"}
                                   label={ props.intl.formatMessage({ id:"IDS_COMMENTS"})}                    
                                   placeholder={ props.intl.formatMessage({ id:"IDS_COMMENTS"})}
                                   value ={ props.selectedRecord["scomments"] || ""}
                                   rows="2"
                                   isMandatory={false}
                                   required={false}
                                   maxLength={255}
                                   onChange={(event)=> props.onInputOnChange(event)}
                                   />
                         </Col>         
                   </Row>
               </Col>              
          </Row>   
       )
   }
   export default injectIntl(AddGoodsIn);
