import React from 'react';
import { Row, Col ,FormGroup } from 'react-bootstrap';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import FormInput from '../../components/form-input/form-input.component';
import { ReadOnlyText } from '../../components/App.styles';

import { injectIntl } from 'react-intl';


const AddGrossQuotation = (props) => {
    return (
        <Row>
            <Col md={4}>

                <Row>
                   <Col md={12}>
                       <FormSelectSearch
                            formLabel={props.intl.formatMessage({ id: "IDS_DISCOUNTNAME" })}
                            isSearchable={true}
                            name={"ndiscountbandcode"}
                            isDisabled={false}
                            //     placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                            isMandatory={true}
                            isClearable={false}
                            options={props.DiscountBand}
                            value={props.selectedRecord["ndiscountbandcode"] || ""}
                            defaultValue={props.selectedRecord["ndiscountbandcode"]}
                            onChange={(event) => props.onComboChange(event, "ndiscountbandcode", 3)}
                         //    onBlur={() => props.onnetDiscountEvent(props.selectedRecord)}
                            closeMenuOnSelect={true}
                        >
                        </FormSelectSearch>
                    </Col>

                    <Col md={12}>
                    <FormSelectSearch
                            formLabel={props.intl.formatMessage({ id: "IDS_VATNAME" })}
                            isSearchable={true}
                            name={"nvatbandcode"}
                            isDisabled={false}
                     //     placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                            isMandatory={true}
                            isClearable={false}
                            options={props.VATBand}
                            value={props.selectedRecord["nvatbandcode"] || ""}
                       //     value={props.SelectedClient? { "label": props.SelectedClient.sclientname, "value": props.SelectedClient.nclientcode } : ""}
                            defaultValue={props.selectedRecord["nvatbandcode"]}
                            onChange={(event) => props.onComboChange(event, "nvatbandcode", 3)}
                            closeMenuOnSelect={true}
                        >
                        </FormSelectSearch>
                    </Col>


                </Row>
            </Col>


            <Col md={4}>
                <Row>

                <Col md={12}>

                <FormInput
                            label={props.intl.formatMessage({ id: "IDS_DISCOUNTPERCENTAGE" })}
                            name={"namount"}
                            type="text"
                            onChange={(event) => props.onInputOnChange(event)}
                            placeholder={props.intl.formatMessage({ id: "IDS_DISCOUNTPERCENTAGE" })}
                            // value={props.selectedRecord ? props.selectedRecord["namount"] : ""}
                         //   value={props.selectedRecord.ndiscountbandcode ? props.selectedRecord.ndiscountbandcode.item["namount"] : ""}
                            value={props.selectedRecord.ndiscountpercentage ? props.selectedRecord.ndiscountpercentage : ""}
                            isMandatory={true}
                            isDisabled={true}
                            required={true}
                            maxLength={"100"}
                        />
                </Col>  

                    <Col md={12}>

                  <FormInput
                            label={props.intl.formatMessage({ id: "IDS_VATPERCENTAGE" })}
                            name={"namount"}
                            type="text"
                            onChange={(event) => props.onInputOnChange(event)}
                            placeholder={props.intl.formatMessage({ id: "IDS_VATPERCENTAGE" })}
                            // value={props.selectedRecord ? props.selectedRecord["namount"] : ""}
                     //       value={props.selectedRecord.nvatbandcode ? props.selectedRecord.nvatbandcode.item["namount"] : ""}
                            value={props.selectedRecord.nvatpercentage ? props.selectedRecord.nvatpercentage : ""}
                            isMandatory={true}
                            isDisabled={true}
                            required={true}
                            maxLength={"100"}
                        />

                    </Col>

                </Row>
            </Col>

            <Col md={4}>

                <Row>
                    <Col md={12}>
                    <FormInput
                            label={props.intl.formatMessage({ id: "IDS_TOTALGROSSAMOUNT" })}
                            name={"ntotalgrossamount"}
                            type="text"
                            onChange={(event) => props.onInputOnChange(event)}
                            placeholder={props.intl.formatMessage({ id: "IDS_TOTALGROSSAMOUNT" })}
                            value={props.QuotationGrossAmount ? props.QuotationGrossAmount[0]["ntotalgrossamount"] : ""}
                            isMandatory={true}
                            required={true}
                            maxLength={"100"}
                            isDisabled={true}
                        />
                    </Col>

                   

                    <Col md={12}>
                    <FormInput
                            label={props.intl.formatMessage({ id: "IDS_DISCOUNTAMOUNT" })}
                            name={"ndiscountbandcode"}
                            type="text"
                            onChange={(event) => props.onInputOnChange(event)}
                            placeholder={props.intl.formatMessage({ id: "IDS_DISCOUNTAMOUNT" })}
                            value={props.DiscountAmount}
                            isMandatory={true}
                            required={true}
                            maxLength={"100"}
                            isDisabled={true}
                        />
                    </Col>

                    <Col md={12}>
                        <FormInput
                                label={props.intl.formatMessage({ id: "IDS_VATAMOUNT" })}
                                name={"nvatbandcode"}
                                type="text"
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_VATAMOUNT" })}
                                value={props.VatAmount}
                                isMandatory={true}
                                required={true}
                                maxLength={"100"}
                                isDisabled={true}
                            />
                    </Col>
                   

                   
                    <Col md={12}>
                        <FormInput
                                label={props.intl.formatMessage({ id: "IDS_TOTALNETAMOUNT" })}
                                name={"ntotalnetamount"}
                                type="text"
                                onChange={(event) => props.onInputOnChange(event)}
                                placeholder={props.intl.formatMessage({ id: "IDS_TOTALNETAMOUNT" })}
                                value={props.TotalNetAmount}
                                isMandatory={true}
                                required={true}
                                maxLength={"100"}
                                isDisabled={true}
                            />
                    </Col>
                   
                </Row>
            </Col>

        </Row>

    );

}




export default injectIntl(AddGrossQuotation);
