    showQueryParameter = () => {
     //   let newObject = {}
        let returnString = "{";
        this.state.constructApiData.map((e, index) => {
            if (index < 2) {
                returnString = returnString + " \n \"" + Object.keys(e) + "\":\"" + Object.values(e)+"\""
            }

        })
        let text = this.filterQueryFormation(this.state.selectedRecord)

        returnString = returnString + "\n" + " \"whereCondition\":\"" + text+"\"\n}"
        // const object = {
        //     ...newObject,
        //     "whereCondition": text
        // }

        return returnString;
    }

    copyParameter = () => {
        let newObject = {}
        this.state.constructApiData.map((e, index) => {
            if (index < 2) {
                newObject = {
                    ...newObject,
                    ...e
                }
            }

        })
        let text = this.filterQueryFormation(this.state.selectedRecord)
        const object = {
            ...newObject,
            "whereCondition": text
        }

        return JSON.stringify(object);
    }

    sendObject = () => {
        let newObject = {}
        this.state.constructApiData.map(e => {
            newObject = {
                ...newObject,
                ...e
            }
        })
        let text = this.filterQueryFormation(this.state.selectedRecord)
        const object = {
            ...newObject,
            "whereCondition": text
        }

        return object;
    }

    componentDidUpdate(previousProps) {
        if (this.props.Login.masterData !== previousProps.Login.masterData) {
            console.log(this.props.Login.masterData !== previousProps.Login.masterData)
            let { userRoleControlRights, controlMap, apiServiceList,
                selectedRecord, constructApiData, lstQueryBuilder, qualisList
            } = { ...this.state }

            if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
