                          <Col md={4}>
                            <FormGroup>
                              <FormLabel>
                                <FormattedMessage
                                  id="IDS_FATHERGUARDIANNAME"
                                  message="Father/Guardian Name"
                                />
                              </FormLabel>
                              <ReadOnlyText>
                                {
                                  this.props.Login.masterData.SelectedPatient
                                    .sfathername
                                }
                              </ReadOnlyText>
                            </FormGroup>
                          </Col>
                          <Col md={4}>
                            <FormGroup>
                              <FormLabel>
                                <FormattedMessage
                                  id="IDS_PHONENO"
                                  message="Phone No"
                                />
                              </FormLabel>
                              <ReadOnlyText>
                                {
                                  this.props.Login.masterData.SelectedPatient
                                    .sphoneno
                                }
                              </ReadOnlyText>
                            </FormGroup>
                          </Col>

                          <Col md={8}>
                            <FormGroup>
                              <FormLabel>
                                <FormattedMessage
                                  id="IDS_ADDRESS"
                                  message="Address"
                                />
                              </FormLabel>
                              <ReadOnlyText>
                                {this.props.Login.masterData.SelectedPatient
                                  .saddress === null ||
                                  this.props.Login.masterData.SelectedPatient
                                    .saddress.length === 0
                                  ? "-"
                                  : this.props.Login.masterData.SelectedPatient
                                    .saddress}
